pragma solidity ^0.8.16;

import {HubDependent} from "../abstract/HubDependent.sol";
import {Named} from "../interface/Named.sol";
import {Versioned} from "../interface/Versioned.sol";
import {AssertionStructs} from "../structs/AssertionStructs.sol";

contract AssertionStorage is Named, Versioned, HubDependent {
    string private constant _NAME = "AssertionStorage";
    string private constant _VERSION = "1.0.0";

    // assertionId => Assertion
    mapping(bytes32 => AssertionStructs.Assertion) internal assertions;

    // solhint-disable-next-line no-empty-blocks
    constructor(address hubAddress) HubDependent(hubAddress) {}

    function name() external pure virtual override returns (string memory) {
        return _NAME;
    }

    function version() external pure virtual override returns (string memory) {
        return _VERSION;
    }

    function createAssertion(
        bytes32 assertionId,
        uint128 size,
        uint32 triplesNumber,
        uint96 chunksNumber
    ) external onlyContracts {
        assertions[assertionId] = AssertionStructs.Assertion({
            timestamp: block.timestamp,
            size: size,
            triplesNumber: triplesNumber,
            chunksNumber: chunksNumber
        });
    }

    function deleteAssertion(bytes32 assertionId) external onlyContracts {
        delete assertions[assertionId];
    }

    function getAssertion(bytes32 assertionId) external view returns (AssertionStructs.Assertion memory) {
        return assertions[assertionId];
    }

    function getAssertionTimestamp(bytes32 assertionId) external view returns (uint256) {
        return assertions[assertionId].timestamp;
    }

    function getAssertionSize(bytes32 assertionId) external view returns (uint128) {
        return assertions[assertionId].size;
    }

    function getAssertionTriplesNumber(bytes32 assertionId) external view returns (uint32) {
        return assertions[assertionId].triplesNumber;
    }

    function getAssertionChunksNumber(bytes32 assertionId) external view returns (uint96) {
        return assertions[assertionId].chunksNumber;
    }

    function assertionExists(bytes32 assertionId) external view returns (bool) {
        return assertions[assertionId].timestamp != 0;
    }
}
","keccak256":"0xf523695eba20ece14bab74fc2581e01d7b9f02220c3d0577fc5f4eafeb4eeb88","license":"MIT"},"contracts/storage/IdentityStorage.sol":{"content":"// SPDX-License-Identifier: MIT

pragma solidity ^0.8.16;

import {HubDependent} from "../abstract/HubDependent.sol";
import {IERC734Extended} from "../interface/IERC734Extended.sol";
import {Named} from "../interface/Named.sol";
import {Versioned} from "../interface/Versioned.sol";
import {ByteArr} from "../utils/ByteArr.sol";
import {OPERATIONAL_KEY} from "../constants/IdentityConstants.sol";

contract IdentityStorage is IERC734Extended, Named, Versioned, HubDependent {
    using ByteArr for bytes32[];

    string private constant _NAME = "IdentityStorage";
    string private constant _VERSION = "1.0.0";

    uint72 private _identityId;

    struct Identity {
        mapping(bytes32 => Key) keys;
        mapping(uint256 => bytes32[]) keysByPurpose;
    }

    // operationalKey => identityId
    mapping(bytes32 => uint72) public identityIds;
    // identityId => Identity
    mapping(uint72 => Identity) internal identities;

    constructor(address hubAddress) HubDependent(hubAddress) {
        _identityId = 1;
    }

    function name() external pure virtual override returns (string memory) {
        return _NAME;
    }

    function version() external pure virtual override returns (string memory) {
        return _VERSION;
    }

    function deleteIdentity(uint72 identityId) external onlyContracts {
        bytes32[] memory operationalKeys = identities[identityId].keysByPurpose[OPERATIONAL_KEY];
        uint256 operationalKeysNumber = operationalKeys.length;

        for (uint256 i; i < operationalKeysNumber; ) {
            delete identityIds[operationalKeys[i]];
            unchecked {
                i++;
            }
        }

        delete identities[identityId];
    }

    function addKey(uint72 identityId, bytes32 _key, uint256 _purpose, uint256 _type) external override onlyContracts {
        Identity storage identity = identities[identityId];
        identity.keys[_key].purpose = _purpose;
        identity.keys[_key].keyType = _type;
        identity.keys[_key].key = _key;
        identity.keysByPurpose[_purpose].push(_key);

        emit KeyAdded(identityId, _key, _purpose, _type);
    }

    function removeKey(uint72 identityId, bytes32 _key) external override onlyContracts {
        Identity storage identity = identities[identityId];

        uint256 index;
        (index, ) = identity.keysByPurpose[identity.keys[_key].purpose].indexOf(_key);
        identity.keysByPurpose[identity.keys[_key].purpose].removeByIndex(index);

        delete identity.keys[_key];

        emit KeyRemoved(identityId, identity.keys[_key].key, identity.keys[_key].purpose, identity.keys[_key].keyType);
    }

    function keyHasPurpose(uint72 identityId, bytes32 _key, uint256 _purpose) external view override returns (bool) {
        return identities[identityId].keys[_key].purpose == _purpose;
    }

    function getKey(uint72 identityId, bytes32 _key) external view override returns (uint256, uint256, bytes32) {
        return (
            identities[identityId].keys[_key].purpose,
            identities[identityId].keys[_key].keyType,
            identities[identityId].keys[_key].key
        );
    }

    function getKeysByPurpose(uint72 identityId, uint256 _purpose) external view override returns (bytes32[] memory) {
        return identities[identityId].keysByPurpose[_purpose];
    }

    function getIdentityId(address operational) external view returns (uint72) {
        return identityIds[keccak256(abi.encodePacked(operational))];
    }

    function setOperationalKeyIdentityId(bytes32 operationalKey, uint72 identityId) external onlyContracts {
        identityIds[operationalKey] = identityId;
    }

    function removeOperationalKeyIdentityId(bytes32 operationalKey) external onlyContracts {
        delete identityIds[operationalKey];
    }

    function generateIdentityId() external onlyContracts returns (uint72) {
        unchecked {
            return _identityId++;
        }
    }
}
","keccak256":"0x50d2f7a594213a4f2b6bf35019bafbab59b6a6b7cb151680031d3c8db572a0b1","license":"MIT"},"contracts/storage/ParametersStorage.sol":{"content":"// SPDX-License-Identifier: MIT

pragma solidity ^0.8.16;

import {HubDependent} from "../abstract/HubDependent.sol";
import {Named} from "../interface/Named.sol";
import {Versioned} from "../interface/Versioned.sol";

contract ParametersStorage is Named, Versioned, HubDependent {
    event ParameterChanged(string parameterName, uint256 parameterValue);

    string private constant _NAME = "ParametersStorage";
    string private constant _VERSION = "1.1.0";

    // 0 - minProofWindowOffsetPerc
    // 1 - maxProofWindowOffsetPerc
    // 2 - commitWindowDurationPerc
    // 3 - proofWindowDurationPerc
    // 4 - replacementWindowDurationPerc
    // 5 - finalizationCommitsNumber
    uint8[6] internal args1;

    // 0 - r0
    // 1 - r1
    // 2 - slashingFreezeDuration
    uint32[3] internal args2;
    uint48 public r2;

    // 0 - minimumStake
    // 1 - maximumStake
    uint96[2] internal args3;

    uint128 public epochLength;

    // 0 - stakeWithdrawalDelay
    // 1 - rewardWithdrawalDelay
    uint24[2] internal args4;

    uint16 public updateCommitWindowDuration;

    constructor(address hubAddress) HubDependent(hubAddress) {
        // minimumStake
        args3[0] = 50_000 ether;
        // maximumStake
        args3[1] = 5_000_000 ether;

        r2 = 20;
        // r1
        args2[1] = 8;
        // r0
        args2[0] = 3;

        // minProofWindowOffsetPerc
        args1[0] = 50;
        // maxProofWindowOffsetPerc
        args1[1] = 75;
        // commitWindowDurationPerc
        args1[2] = 25;
        // proofWindowDurationPerc
        args1[3] = 25;
        // replacementWindowDurationPerc
        args1[4] = 0;

        epochLength = 90 days;

        // stakeWithdrawalDelay
        args4[0] = 5 minutes;
        // rewardWithdrawalDelay
        args4[1] = 5 minutes;
        // slashingFreezeDuration
        args2[2] = 730 days;

        updateCommitWindowDuration = 30 minutes;

        // finalizationCommitsNumber
        args1[5] = 3;
    }

    function name() external pure virtual override returns (string memory) {
        return _NAME;
    }

    function version() external pure virtual override returns (string memory) {
        return _VERSION;
    }

    function minimumStake() external view returns (uint96) {
        return args3[0];
    }

    function setMinimumStake(uint96 newMinimumStake) external onlyHubOwner {
        args3[0] = newMinimumStake;

        emit ParameterChanged("minimumStake", newMinimumStake);
    }

    function maximumStake() external view returns (uint96) {
        return args3[1];
    }

    function setMaximumStake(uint96 newMaximumStake) external onlyHubOwner {
        args3[1] = newMaximumStake;

        emit ParameterChanged("maximumStake", newMaximumStake);
    }

    function setR2(uint48 newR2) external onlyHubOwner {
        r2 = newR2;

        emit ParameterChanged("r2", newR2);
    }

    function r1() external view returns (uint32) {
        return args2[1];
    }

    function setR1(uint32 newR1) external onlyHubOwner {
        require(newR1 >= (2 * args2[0] - 1), "R1 should be >= 2*R0-1");

        args2[1] = newR1;

        emit ParameterChanged("r1", newR1);
    }

    function r0() external view returns (uint32) {
        return args2[0];
    }

    function setR0(uint32 newR0) external onlyHubOwner {
        require(newR0 <= ((args2[1] + 1) / 2), "R0 should be <= (R1+1)/2");

        args2[0] = newR0;

        emit ParameterChanged("r0", newR0);
    }

    function minProofWindowOffsetPerc() external view returns (uint8) {
        return args1[0];
    }

    function setMinProofWindowOffsetPerc(uint8 newMinProofWindowOffsetPerc) external onlyHubOwner {
        args1[0] = newMinProofWindowOffsetPerc;

        emit ParameterChanged("minProofWindowOffsetPerc", newMinProofWindowOffsetPerc);
    }

    function maxProofWindowOffsetPerc() external view returns (uint8) {
        return args1[1];
    }

    function setMaxProofWindowOffsetPerc(uint8 newMaxProofWindowOffsetPerc) external onlyHubOwner {
        args1[1] = newMaxProofWindowOffsetPerc;

        emit ParameterChanged("maxProofWindowOffsetPerc", newMaxProofWindowOffsetPerc);
    }

    function commitWindowDurationPerc() external view returns (uint8) {
        return args1[2];
    }

    function setCommitWindowDurationPerc(uint8 newCommitWindowDurationPerc) external onlyHubOwner {
        args1[2] = newCommitWindowDurationPerc;

        emit ParameterChanged("commitWindowDurationPerc", newCommitWindowDurationPerc);
    }

    function proofWindowDurationPerc() external view returns (uint8) {
        return args1[3];
    }

    function setProofWindowDurationPerc(uint8 newProofWindowDurationPerc) external onlyHubOwner {
        args1[3] = newProofWindowDurationPerc;

        emit ParameterChanged("proofWindowDurationPerc", newProofWindowDurationPerc);
    }

    function replacementWindowDurationPerc() external view returns (uint8) {
        return args1[4];
    }

    function setReplacementWindowDurationPerc(uint8 newReplacementWindowDurationPerc) external onlyHubOwner {
        args1[4] = newReplacementWindowDurationPerc;

        emit ParameterChanged("replacementWindowDurationPerc", newReplacementWindowDurationPerc);
    }

    function setEpochLength(uint128 newEpochLength) external onlyHubOwner {
        epochLength = newEpochLength;

        emit ParameterChanged("epochLength", newEpochLength);
    }

    function stakeWithdrawalDelay() external view returns (uint24) {
        return args4[0];
    }

    function setStakeWithdrawalDelay(uint24 newStakeWithdrawalDelay) external onlyHubOwner {
        args4[0] = newStakeWithdrawalDelay;

        emit ParameterChanged("stakeWithdrawalDelay", newStakeWithdrawalDelay);
    }

    function rewardWithdrawalDelay() external view returns (uint24) {
        return args4[1];
    }

    function setRewardWithdrawalDelay(uint24 newRewardWithdrawalDelay) external onlyHubOwner {
        args4[1] = newRewardWithdrawalDelay;

        emit ParameterChanged("rewardWithdrawalDelay", newRewardWithdrawalDelay);
    }

    function slashingFreezeDuration() external view returns (uint32) {
        return args2[2];
    }

    function setSlashingFreezeDuration(uint32 newSlashingFreezeDuration) external onlyHubOwner {
        args2[2] = newSlashingFreezeDuration;

        emit ParameterChanged("slashingFreezeDuration", newSlashingFreezeDuration);
    }

    function setUpdateCommitWindowDuration(uint16 newUpdateCommitWindowDuration) external onlyHubOwner {
        updateCommitWindowDuration = newUpdateCommitWindowDuration;

        emit ParameterChanged("updateCommitWindowDuration", newUpdateCommitWindowDuration);
    }

    function finalizationCommitsNumber() external view returns (uint8) {
        return args1[5];
    }

    function setFinalizationCommitsNumber(uint8 newFinalizationCommitsNumber) external onlyHubOwner {
        args1[5] = newFinalizationCommitsNumber;

        emit ParameterChanged("finalizationCommitsNumber", newFinalizationCommitsNumber);
    }
}
","keccak256":"0xcf85cdc898aa106de1841fc603229d8a8985c1bed128e8efd3c2e827b9c0cca1","license":"MIT"},"contracts/storage/ProfileStorage.sol":{"content":"// SPDX-License-Identifier: MIT

pragma solidity ^0.8.16;

import {Guardian} from "../Guardian.sol";
import {Shares} from "../Shares.sol";
import {Named} from "../interface/Named.sol";
import {Versioned} from "../interface/Versioned.sol";

contract ProfileStorage is Named, Versioned, Guardian {
    string private constant _NAME = "ProfileStorage";
    string private constant _VERSION = "1.0.0";

    struct ProfileDefinition {
        bytes nodeId;
        uint96 ask;
        uint96 accumulatedOperatorFee;
        uint96 accumulatedOperatorFeeWithdrawalAmount;
        uint256 operatorFeeWithdrawalTimestamp;
        address sharesContractAddress;
        mapping(uint8 => bytes32) nodeAddresses;
    }

    // nodeId => isRegistered?
    mapping(bytes => bool) public nodeIdsList;
    // identityId => Profile
    mapping(uint72 => ProfileDefinition) internal profiles;

    // shares token name => isTaken?
    mapping(string => bool) public sharesNames;
    // shares token ID => isTaken?
    mapping(string => bool) public sharesSymbols;

    // solhint-disable-next-line no-empty-blocks
    constructor(address hubAddress) Guardian(hubAddress) {}

    function name() external pure virtual override returns (string memory) {
        return _NAME;
    }

    function version() external pure virtual override returns (string memory) {
        return _VERSION;
    }

    function createProfile(
        uint72 identityId,
        bytes calldata nodeId,
        address sharesContractAddress
    ) external onlyContracts {
        ProfileDefinition storage profile = profiles[identityId];
        profile.nodeId = nodeId;
        profile.sharesContractAddress = sharesContractAddress;

        nodeIdsList[nodeId] = true;

        Shares sharesContract = Shares(sharesContractAddress);
        sharesNames[sharesContract.name()] = true;
        sharesSymbols[sharesContract.symbol()] = true;
    }

    function getProfile(
        uint72 identityId
    ) external view returns (bytes memory nodeId, uint96[2] memory profileSettings, address sharesContractAddress) {
        ProfileDefinition storage profile = profiles[identityId];
        return (profile.nodeId, [profile.ask, profile.accumulatedOperatorFee], profile.sharesContractAddress);
    }

    function deleteProfile(uint72 identityId) external onlyContracts {
        nodeIdsList[profiles[identityId].nodeId] = false;
        delete profiles[identityId];
    }

    function getNodeId(uint72 identityId) external view returns (bytes memory) {
        return profiles[identityId].nodeId;
    }

    function setNodeId(uint72 identityId, bytes calldata nodeId) external onlyContracts {
        ProfileDefinition storage profile = profiles[identityId];

        nodeIdsList[profile.nodeId] = false;
        profile.nodeId = nodeId;
        nodeIdsList[nodeId] = true;
    }

    function getAsk(uint72 identityId) external view returns (uint96) {
        return profiles[identityId].ask;
    }

    function setAsk(uint72 identityId, uint96 ask) external onlyContracts {
        profiles[identityId].ask = ask;
    }

    function getAccumulatedOperatorFee(uint72 identityId) external view returns (uint96) {
        return profiles[identityId].accumulatedOperatorFee;
    }

    function setAccumulatedOperatorFee(uint72 identityId, uint96 newOperatorFeeAmount) external onlyContracts {
        profiles[identityId].accumulatedOperatorFee = newOperatorFeeAmount;
    }

    function getAccumulatedOperatorFeeWithdrawalAmount(uint72 identityId) external view returns (uint96) {
        return profiles[identityId].accumulatedOperatorFeeWithdrawalAmount;
    }

    function setAccumulatedOperatorFeeWithdrawalAmount(
        uint72 identityId,
        uint96 accumulatedOperatorFeeWithdrawalAmount
    ) external onlyContracts {
        profiles[identityId].accumulatedOperatorFeeWithdrawalAmount = accumulatedOperatorFeeWithdrawalAmount;
    }

    function getAccumulatedOperatorFeeWithdrawalTimestamp(uint72 identityId) external view returns (uint256) {
        return profiles[identityId].operatorFeeWithdrawalTimestamp;
    }

    function setAccumulatedOperatorFeeWithdrawalTimestamp(
        uint72 identityId,
        uint256 operatorFeeWithdrawalTimestamp
    ) external onlyContracts {
        profiles[identityId].operatorFeeWithdrawalTimestamp = operatorFeeWithdrawalTimestamp;
    }

    function getSharesContractAddress(uint72 identityId) external view returns (address) {
        return profiles[identityId].sharesContractAddress;
    }

    function setSharesContractAddress(uint72 identityId, address sharesContractAddress) external onlyContracts {
        profiles[identityId].sharesContractAddress = sharesContractAddress;
    }

    function getNodeAddress(uint72 identityId, uint8 hashFunctionId) external view returns (bytes32) {
        return profiles[identityId].nodeAddresses[hashFunctionId];
    }

    function setNodeAddress(uint72 identityId, uint8 hashFunctionId, bytes32 nodeAddress) external onlyContracts {
        profiles[identityId].nodeAddresses[hashFunctionId] = nodeAddress;
    }

    function profileExists(uint72 identityId) external view returns (bool) {
        return keccak256(profiles[identityId].nodeId) != keccak256(bytes(""));
    }

    function transferAccumulatedOperatorFee(address receiver, uint96 amount) external onlyContracts {
        tokenContract.transfer(receiver, amount);
    }
}
","keccak256":"0x658280fecb28870447d89924e8114b8847a3107b674ca5c27d402499b1921332","license":"MIT"},"contracts/storage/ServiceAgreementStorageProxy.sol":{"content":"// SPDX-License-Identifier: MIT

pragma solidity ^0.8.16;

import {ServiceAgreementStorageV1} from "./ServiceAgreementStorageV1.sol";
import {ServiceAgreementStorageV1U1} from "./ServiceAgreementStorageV1U1.sol";
import {ServiceAgreementStructsV1} from "../structs/ServiceAgreementStructsV1.sol";
import {HubDependent} from "../abstract/HubDependent.sol";
import {Initializable} from "../interface/Initializable.sol";
import {Named} from "../interface/Named.sol";
import {Versioned} from "../interface/Versioned.sol";
import {GeneralErrors} from "../errors/GeneralErrors.sol";

contract ServiceAgreementStorageProxy is Named, Versioned, HubDependent, Initializable {
    string private constant _NAME = "ServiceAgreementStorageProxy";
    string private constant _VERSION = "1.0.0";

    ServiceAgreementStorageV1 public storageV1;
    ServiceAgreementStorageV1U1 public storageV1U1;

    // solhint-disable-next-line no-empty-blocks
    constructor(address hubAddress) HubDependent(hubAddress) {}

    function initialize() public onlyHubOwner {
        storageV1 = ServiceAgreementStorageV1(hub.getContractAddress("ServiceAgreementStorageV1"));
        storageV1U1 = ServiceAgreementStorageV1U1(hub.getContractAddress("ServiceAgreementStorageV1U1"));
    }

    function name() external pure virtual override returns (string memory) {
        return _NAME;
    }

    function version() external pure virtual override returns (string memory) {
        return _VERSION;
    }

    function migrateV1ServiceAgreement(bytes32 agreementId) external onlyContracts {
        ServiceAgreementStorageV1 sasV1 = storageV1;
        ServiceAgreementStorageV1U1 sasV1U1 = storageV1U1;

        uint96 tokenAmount = sasV1.getAgreementTokenAmount(agreementId);

        sasV1.deleteServiceAgreementObject(agreementId);

        sasV1U1.setAgreementTokenAmount(agreementId, tokenAmount);
        sasV1.transferAgreementTokens(address(sasV1U1), tokenAmount);
    }

    function createV1U1ServiceAgreementObject(
        bytes32 agreementId,
        uint256 startTime,
        uint16 epochsNumber,
        uint128 epochLength,
        uint96 tokenAmount,
        uint8 scoreFunctionId,
        uint8 proofWindowOffsetPerc
    ) external onlyContracts {
        storageV1U1.createServiceAgreementObject(
            agreementId,
            startTime,
            epochsNumber,
            epochLength,
            tokenAmount,
            scoreFunctionId,
            proofWindowOffsetPerc
        );
    }

    function createV1ServiceAgreementObject(
        bytes32 agreementId,
        uint16 epochsNumber,
        uint128 epochLength,
        uint96 tokenAmount,
        uint8 scoreFunctionId,
        uint8 proofWindowOffsetPerc
    ) external onlyContracts {
        storageV1.createServiceAgreementObject(
            agreementId,
            epochsNumber,
            epochLength,
            tokenAmount,
            scoreFunctionId,
            proofWindowOffsetPerc
        );
    }

    function deleteServiceAgreementObject(bytes32 agreementId) external onlyContracts {
        if (this.agreementV1Exists(agreementId)) {
            storageV1.deleteServiceAgreementObject(agreementId);
        } else {
            storageV1U1.deleteServiceAgreementObject(agreementId);
        }
    }

    function getAgreementData(
        bytes32 agreementId
    )
        external
        view
        returns (
            uint256 startTime,
            uint16 epochsNumber,
            uint128 epochLength,
            uint96[2] memory tokens,
            uint8[2] memory scoreFunctionIdAndProofWindowOffsetPerc
        )
    {
        if (this.agreementV1Exists(agreementId)) {
            uint96 tokenAmount;
            (startTime, epochsNumber, epochLength, tokenAmount, scoreFunctionIdAndProofWindowOffsetPerc) = storageV1
                .getAgreementData(agreementId);
            return (
                startTime,
                epochsNumber,
                epochLength,
                [tokenAmount, storageV1U1.getAgreementUpdateTokenAmount(agreementId)],
                scoreFunctionIdAndProofWindowOffsetPerc
            );
        } else {
            return storageV1U1.getAgreementData(agreementId);
        }
    }

    function getAgreementStartTime(bytes32 agreementId) external view returns (uint256) {
        if (this.agreementV1Exists(agreementId)) {
            return storageV1.getAgreementStartTime(agreementId);
        } else {
            return storageV1U1.getAgreementStartTime(agreementId);
        }
    }

    function setAgreementStartTime(bytes32 agreementId, uint256 startTime) external onlyContracts {
        if (this.agreementV1Exists(agreementId)) {
            storageV1.setAgreementStartTime(agreementId, startTime);
        } else {
            storageV1U1.setAgreementStartTime(agreementId, startTime);
        }
    }

    function getAgreementEpochsNumber(bytes32 agreementId) external view returns (uint16) {
        if (this.agreementV1Exists(agreementId)) {
            return storageV1.getAgreementEpochsNumber(agreementId);
        } else {
            return storageV1U1.getAgreementEpochsNumber(agreementId);
        }
    }

    function setAgreementEpochsNumber(bytes32 agreementId, uint16 epochsNumber) external onlyContracts {
        if (this.agreementV1Exists(agreementId)) {
            storageV1.setAgreementEpochsNumber(agreementId, epochsNumber);
        } else {
            storageV1U1.setAgreementEpochsNumber(agreementId, epochsNumber);
        }
    }

    function getAgreementEpochLength(bytes32 agreementId) external view returns (uint128) {
        if (this.agreementV1Exists(agreementId)) {
            return storageV1.getAgreementEpochLength(agreementId);
        } else {
            return storageV1U1.getAgreementEpochLength(agreementId);
        }
    }

    function setAgreementEpochLength(bytes32 agreementId, uint128 epochLength) external onlyContracts {
        if (this.agreementV1Exists(agreementId)) {
            storageV1.setAgreementEpochLength(agreementId, epochLength);
        } else {
            storageV1U1.setAgreementEpochLength(agreementId, epochLength);
        }
    }

    function getAgreementTokenAmount(bytes32 agreementId) external view returns (uint96) {
        if (this.agreementV1Exists(agreementId)) {
            return storageV1.getAgreementTokenAmount(agreementId);
        } else {
            return storageV1U1.getAgreementTokenAmount(agreementId);
        }
    }

    function setAgreementTokenAmount(bytes32 agreementId, uint96 tokenAmount) external onlyContracts {
        if (this.agreementV1Exists(agreementId)) {
            storageV1.setAgreementTokenAmount(agreementId, tokenAmount);
        } else {
            storageV1U1.setAgreementTokenAmount(agreementId, tokenAmount);
        }
    }

    function getAgreementUpdateTokenAmount(bytes32 agreementId) external view returns (uint96) {
        return storageV1U1.getAgreementUpdateTokenAmount(agreementId);
    }

    function setAgreementUpdateTokenAmount(bytes32 agreementId, uint96 updateTokenAmount) external onlyContracts {
        storageV1U1.setAgreementUpdateTokenAmount(agreementId, updateTokenAmount);
