    }

    function deleteIssuer(uint256 tokenId) external onlyContracts {
        delete issuers[tokenId];
    }

    function hasPendingUpdate(uint256 tokenId) external view returns (bool) {
        return unfinalizedStates[tokenId] != bytes32(0);
    }
