    /// @notice Calculates the binary logarithm of x.
    ///
    /// @dev Based on the iterative approximation algorithm.
    /// https://en.wikipedia.org/wiki/Binary_logarithm#Iterative_approximation
    ///
    /// Requirements:
    /// - x must be greater than or equal to SCALE, otherwise the result would be negative.
    ///
    /// Caveats:
    /// - The results are nor perfectly accurate to the last decimal, due to the lossy precision of the iterative approximation.
    ///
    /// @param x The unsigned 60.18-decimal fixed-point number for which to calculate the binary logarithm.
    /// @return result The binary logarithm as an unsigned 60.18-decimal fixed-point number.
    function log2(uint256 x) internal pure returns (uint256 result) {
        if (x < SCALE) {
            revert PRBMathUD60x18__LogInputTooSmall(x);
        }
        unchecked {
            // Calculate the integer part of the logarithm and add it to the result and finally calculate y = x * 2^(-n).
            uint256 n = PRBMath.mostSignificantBit(x / SCALE);

            // The integer part of the logarithm as an unsigned 60.18-decimal fixed-point number. The operation can't overflow
            // because n is maximum 255 and SCALE is 1e18.
            result = n * SCALE;

            // This is y = x * 2^(-n).
            uint256 y = x >> n;

            // If y = 1, the fractional part is zero.
            if (y == SCALE) {
                return result;
            }

            // Calculate the fractional part via the iterative approximation.
            // The "delta >>= 1" part is equivalent to "delta /= 2", but shifting bits is faster.
            for (uint256 delta = HALF_SCALE; delta > 0; delta >>= 1) {
                y = (y * y) / SCALE;

                // Is y^2 > 2 and so in the range [2,4)?
                if (y >= 2 * SCALE) {
                    // Add the 2^(-m) factor to the logarithm.
                    result += delta;

                    // Corresponds to z/2 on Wikipedia.
                    y >>= 1;
                }
            }
        }
    }

    /// @notice Multiplies two unsigned 60.18-decimal fixed-point numbers together, returning a new unsigned 60.18-decimal
    /// fixed-point number.
    /// @dev See the documentation for the "PRBMath.mulDivFixedPoint" function.
    /// @param x The multiplicand as an unsigned 60.18-decimal fixed-point number.
    /// @param y The multiplier as an unsigned 60.18-decimal fixed-point number.
    /// @return result The product as an unsigned 60.18-decimal fixed-point number.
    function mul(uint256 x, uint256 y) internal pure returns (uint256 result) {
        result = PRBMath.mulDivFixedPoint(x, y);
    }

    /// @notice Returns PI as an unsigned 60.18-decimal fixed-point number.
    function pi() internal pure returns (uint256 result) {
        result = 3_141592653589793238;
    }

    /// @notice Raises x to the power of y.
    ///
    /// @dev Based on the insight that x^y = 2^(log2(x) * y).
    ///
    /// Requirements:
    /// - All from "exp2", "log2" and "mul".
    ///
    /// Caveats:
    /// - All from "exp2", "log2" and "mul".
    /// - Assumes 0^0 is 1.
    ///
    /// @param x Number to raise to given power y, as an unsigned 60.18-decimal fixed-point number.
    /// @param y Exponent to raise x to, as an unsigned 60.18-decimal fixed-point number.
    /// @return result x raised to power y, as an unsigned 60.18-decimal fixed-point number.
    function pow(uint256 x, uint256 y) internal pure returns (uint256 result) {
        if (x == 0) {
            result = y == 0 ? SCALE : uint256(0);
        } else {
            result = exp2(mul(log2(x), y));
        }
    }

    /// @notice Raises x (unsigned 60.18-decimal fixed-point number) to the power of y (basic unsigned integer) using the
    /// famous algorithm "exponentiation by squaring".
    ///
    /// @dev See https://en.wikipedia.org/wiki/Exponentiation_by_squaring
    ///
    /// Requirements:
    /// - The result must fit within MAX_UD60x18.
    ///
    /// Caveats:
    /// - All from "mul".
    /// - Assumes 0^0 is 1.
    ///
    /// @param x The base as an unsigned 60.18-decimal fixed-point number.
    /// @param y The exponent as an uint256.
    /// @return result The result as an unsigned 60.18-decimal fixed-point number.
    function powu(uint256 x, uint256 y) internal pure returns (uint256 result) {
        // Calculate the first iteration of the loop in advance.
        result = y & 1 > 0 ? x : SCALE;

        // Equivalent to "for(y /= 2; y > 0; y /= 2)" but faster.
        for (y >>= 1; y > 0; y >>= 1) {
            x = PRBMath.mulDivFixedPoint(x, x);

            // Equivalent to "y % 2 == 1" but faster.
            if (y & 1 > 0) {
                result = PRBMath.mulDivFixedPoint(result, x);
            }
        }
    }

    /// @notice Returns 1 as an unsigned 60.18-decimal fixed-point number.
    function scale() internal pure returns (uint256 result) {
        result = SCALE;
    }

    /// @notice Calculates the square root of x, rounding down.
    /// @dev Uses the Babylonian method https://en.wikipedia.org/wiki/Methods_of_computing_square_roots#Babylonian_method.
    ///
    /// Requirements:
    /// - x must be less than MAX_UD60x18 / SCALE.
    ///
    /// @param x The unsigned 60.18-decimal fixed-point number for which to calculate the square root.
    /// @return result The result as an unsigned 60.18-decimal fixed-point .
    function sqrt(uint256 x) internal pure returns (uint256 result) {
        unchecked {
            if (x > MAX_UD60x18 / SCALE) {
                revert PRBMathUD60x18__SqrtOverflow(x);
            }
            // Multiply x by the SCALE to account for the factor of SCALE that is picked up when multiplying two unsigned
            // 60.18-decimal fixed-point numbers together (in this case, those two numbers are both the square root).
            result = PRBMath.sqrt(x * SCALE);
        }
    }

    /// @notice Converts a unsigned 60.18-decimal fixed-point number to basic integer form, rounding down in the process.
    /// @param x The unsigned 60.18-decimal fixed-point number to convert.
    /// @return result The same number in basic integer form.
    function toUint(uint256 x) internal pure returns (uint256 result) {
        unchecked {
            result = x / SCALE;
        }
    }
}

contracts/abstract/AbstractAsset.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.16;

import {HubDependent} from "../abstract/HubDependent.sol";
import {Named} from "../interface/Named.sol";
import {Versioned} from "../interface/Versioned.sol";

abstract contract AbstractAsset is Named, Versioned, HubDependent {
    // solhint-disable-next-line no-empty-blocks
    constructor(address hubAddress) HubDependent(hubAddress) {}

    function getAssertionIds(uint256 tokenId) public view virtual returns (bytes32[] memory);

    function getLatestAssertionId(uint256 tokenId) external view returns (bytes32) {
        bytes32[] memory assertions = getAssertionIds(tokenId);
        return assertions[assertions.length - 1];
    }

    function getAssertionIdByIndex(uint256 tokenId, uint256 index) external view returns (bytes32) {
        bytes32[] memory assertions = getAssertionIds(tokenId);
        return assertions[index];
    }

    function getAssertionIdsLength(uint256 tokenId) external view returns (uint256) {
        return getAssertionIds(tokenId).length;
    }
}

contracts/abstract/ContractStatus.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.16;

import {HubDependent} from "./HubDependent.sol";

abstract contract ContractStatus is HubDependent {
    bool public status;

    // solhint-disable-next-line no-empty-blocks
    constructor(address hubAddress) HubDependent(hubAddress) {}

    function setStatus(bool _status) external onlyHubOwner {
        status = _status;
    }
}

contracts/abstract/HubDependent.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.16;

import {Hub} from "../Hub.sol";

abstract contract HubDependent {
    Hub public hub;

    constructor(address hubAddress) {
        require(hubAddress != address(0), "Hub Address cannot be 0x0");

        hub = Hub(hubAddress);
    }

    modifier onlyHubOwner() {
        _checkHubOwner();
        _;
    }

    modifier onlyContracts() {
        _checkHub();
        _;
    }

    function _checkHubOwner() internal view virtual {
        require(msg.sender == hub.owner(), "Fn can only be used by hub owner");
    }

    function _checkHub() internal view virtual {
        require(hub.isContract(msg.sender), "Fn can only be called by the hub");
    }
}

contracts/Assertion.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.16;

import {AssertionStorage} from "./storage/AssertionStorage.sol";
import {ContractStatus} from "./abstract/ContractStatus.sol";
import {Initializable} from "./interface/Initializable.sol";
import {Named} from "./interface/Named.sol";
import {Versioned} from "./interface/Versioned.sol";
import {AssertionStructs} from "./structs/AssertionStructs.sol";

contract Assertion is Named, Versioned, ContractStatus, Initializable {
    event AssertionCreated(bytes32 indexed assertionId, uint128 size, uint32 triplesNumber, uint96 chunksNumber);

    string private constant _NAME = "Assertion";
    string private constant _VERSION = "1.0.1";

    AssertionStorage public assertionStorage;

    // solhint-disable-next-line no-empty-blocks
    constructor(address hubAddress) ContractStatus(hubAddress) {}

    function initialize() public onlyHubOwner {
        assertionStorage = AssertionStorage(hub.getContractAddress("AssertionStorage"));
    }

    function name() external pure virtual override returns (string memory) {
        return _NAME;
    }

    function version() external pure virtual override returns (string memory) {
        return _VERSION;
    }

    function createAssertion(
        bytes32 assertionId,
        uint128 size,
        uint32 triplesNumber,
        uint96 chunksNumber
    ) external onlyContracts {
        AssertionStorage ans = assertionStorage;

        require(assertionId != bytes32(0), "Assertion ID cannot be empty");
        require(size != 0, "Size cannot be 0");
        require(triplesNumber != 0, "Triples number cannot be 0");
        require(chunksNumber != 0, "Chunks number cannot be 0");

        ans.createAssertion(assertionId, size, triplesNumber, chunksNumber);

        emit AssertionCreated(assertionId, size, triplesNumber, chunksNumber);
    }
}

contracts/assets/ContentAsset.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.16;

import {Assertion} from "../Assertion.sol";
import {HashingProxy} from "../HashingProxy.sol";
import {ServiceAgreementV1} from "../ServiceAgreementV1.sol";
import {ContentAssetStorage} from "../storage/assets/ContentAssetStorage.sol";
import {ParametersStorage} from "../storage/ParametersStorage.sol";
import {ServiceAgreementStorageProxy} from "../storage/ServiceAgreementStorageProxy.sol";
import {UnfinalizedStateStorage} from "../storage/UnfinalizedStateStorage.sol";
import {HubDependent} from "../abstract/HubDependent.sol";
import {Initializable} from "../interface/Initializable.sol";
import {Named} from "../interface/Named.sol";
import {Versioned} from "../interface/Versioned.sol";
import {ContentAssetStructs} from "../structs/assets/ContentAssetStructs.sol";
import {ServiceAgreementStructsV1} from "../structs/ServiceAgreementStructsV1.sol";
import {ContentAssetErrors} from "../errors/assets/ContentAssetErrors.sol";
import {HASH_FUNCTION_ID} from "../constants/assets/ContentAssetConstants.sol";

contract ContentAsset is Named, Versioned, HubDependent, Initializable {
    event AssetMinted(address indexed assetContract, uint256 indexed tokenId, bytes32 indexed state);
    event AssetBurnt(address indexed assetContract, uint256 indexed tokenId, uint96 returnedTokenAmount);
    event AssetStateUpdated(
        address indexed assetContract,
        uint256 indexed tokenId,
        uint256 indexed stateIndex,
        uint96 updateTokenAmount
    );
    event AssetStateUpdateCanceled(
        address indexed assetContract,
        uint256 indexed tokenId,
        uint256 indexed stateIndex,
        uint96 returnedTokenAmount
    );
    event AssetStoringPeriodExtended(
        address indexed assetContract,
        uint256 indexed tokenId,
        uint16 epochsNumber,
        uint96 tokenAmount
    );
    event AssetPaymentIncreased(address indexed assetContract, uint256 indexed tokenId, uint96 tokenAmount);
    event AssetUpdatePaymentIncreased(address indexed assetContract, uint256 indexed tokenId, uint96 tokenAmount);

    string private constant _NAME = "ContentAsset";
    string private constant _VERSION = "1.0.0";

    Assertion public assertionContract;
    HashingProxy public hashingProxy;
    ContentAssetStorage public contentAssetStorage;
    ParametersStorage public parametersStorage;
    ServiceAgreementStorageProxy public serviceAgreementStorageProxy;
    ServiceAgreementV1 public serviceAgreementV1;
    UnfinalizedStateStorage public unfinalizedStateStorage;

    // solhint-disable-next-line no-empty-blocks
    constructor(address hubAddress) HubDependent(hubAddress) {}

    function initialize() public onlyHubOwner {
        assertionContract = Assertion(hub.getContractAddress("Assertion"));
        hashingProxy = HashingProxy(hub.getContractAddress("HashingProxy"));
        contentAssetStorage = ContentAssetStorage(hub.getAssetStorageAddress("ContentAssetStorage"));
        parametersStorage = ParametersStorage(hub.getContractAddress("ParametersStorage"));
        serviceAgreementStorageProxy = ServiceAgreementStorageProxy(
            hub.getContractAddress("ServiceAgreementStorageProxy")
        );
        serviceAgreementV1 = ServiceAgreementV1(hub.getContractAddress("ServiceAgreementV1"));
        unfinalizedStateStorage = UnfinalizedStateStorage(hub.getContractAddress("UnfinalizedStateStorage"));
    }

    modifier onlyAssetOwner(uint256 tokenId) {
        _checkAssetOwner(tokenId);
        _;
    }

    modifier onlyMutable(uint256 tokenId) {
        _checkMutability(tokenId);
        _;
    }

    function name() external pure override returns (string memory) {
        return _NAME;
    }

    function version() external pure override returns (string memory) {
        return _VERSION;
    }

    function createAsset(ContentAssetStructs.AssetInputArgs calldata args) external {
        _createAsset(
            args.assertionId,
            args.size,
            args.triplesNumber,
            args.chunksNumber,
            args.epochsNumber,
            args.tokenAmount,
            args.scoreFunctionId,
            args.immutable_
        );
    }

    function createAssetWithVariables(
        bytes32 assertionId,
        uint128 size,
        uint32 triplesNumber,
        uint96 chunksNumber,
        uint16 epochsNumber,
        uint96 tokenAmount,
        uint8 scoreFunctionId,
        bool immutable_
    ) external {
        _createAsset(
            assertionId,
            size,
            triplesNumber,
            chunksNumber,
            epochsNumber,
            tokenAmount,
            scoreFunctionId,
            immutable_
        );
    }

    function burnAsset(uint256 tokenId) external onlyAssetOwner(tokenId) {
        ContentAssetStorage cas = contentAssetStorage;

        address contentAssetStorageAddress = address(cas);

        bytes memory keyword = abi.encodePacked(contentAssetStorageAddress, cas.getAssertionIdByIndex(tokenId, 0));

        bytes32 agreementId = hashingProxy.callHashFunction(
            HASH_FUNCTION_ID,
            abi.encodePacked(contentAssetStorageAddress, tokenId, keyword)
        );

        bytes32 unfinalizedState = unfinalizedStateStorage.getUnfinalizedState(tokenId);

        if (unfinalizedState != bytes32(0))
            revert ContentAssetErrors.UpdateIsNotFinalized(contentAssetStorageAddress, tokenId, unfinalizedState);

        ServiceAgreementStorageProxy sasProxy = serviceAgreementStorageProxy;
        ParametersStorage params = parametersStorage;

        uint256 timeNow = block.timestamp;
        uint256 epochStart = sasProxy.getAgreementStartTime(agreementId);
        uint256 commitPhaseEnd = epochStart +
            (sasProxy.getAgreementEpochLength(agreementId) * params.commitWindowDurationPerc()) /
            100;
        uint256 epochEnd = epochStart + sasProxy.getAgreementEpochLength(agreementId);
        uint16 epoch = 0;
        uint8 commitsCount = sasProxy.getCommitsCount(
            keccak256(abi.encodePacked(agreementId, epoch, cas.getAssertionIdsLength(tokenId) - 1))
        );
        uint32 r0 = params.r0();

        if ((timeNow < commitPhaseEnd) && (commitsCount < r0)) {
            revert ContentAssetErrors.CommitPhaseOngoing(agreementId);
        } else if ((timeNow < epochEnd) && (commitsCount >= r0)) {
            revert ContentAssetErrors.CommitPhaseSucceeded(agreementId);
        } else if (timeNow > epochEnd) {
            revert ContentAssetErrors.FirstEpochHasAlreadyEnded(agreementId);
        }

        uint96 tokenAmount = sasProxy.getAgreementTokenAmount(agreementId);

        cas.deleteAsset(tokenId);
        cas.burn(tokenId);
        serviceAgreementV1.terminateAgreement(msg.sender, agreementId);

        emit AssetBurnt(contentAssetStorageAddress, tokenId, tokenAmount);
    }

    function updateAssetState(
        uint256 tokenId,
        bytes32 assertionId,
        uint128 size,
        uint32 triplesNumber,
        uint96 chunksNumber,
        uint96 updateTokenAmount
    ) external onlyAssetOwner(tokenId) onlyMutable(tokenId) {
        ContentAssetStorage cas = contentAssetStorage;
        UnfinalizedStateStorage uss = unfinalizedStateStorage;
        ServiceAgreementStorageProxy sasProxy = serviceAgreementStorageProxy;

        address contentAssetStorageAddress = address(cas);

        bytes memory keyword = abi.encodePacked(contentAssetStorageAddress, cas.getAssertionIdByIndex(tokenId, 0));

        bytes32 agreementId = hashingProxy.callHashFunction(
            HASH_FUNCTION_ID,
            abi.encodePacked(contentAssetStorageAddress, tokenId, keyword)
        );

        uint256 startTime;
        uint16 epochsNumber;
        uint128 epochLength;
        uint8[2] memory scoreFunctionIdAndProofWindowOffsetPerc;
        (startTime, epochsNumber, epochLength, , scoreFunctionIdAndProofWindowOffsetPerc) = sasProxy.getAgreementData(
            agreementId
        );

        if (block.timestamp > startTime + epochsNumber * epochLength) revert ContentAssetErrors.AssetExpired(tokenId);

        bytes32 unfinalizedState = uss.getUnfinalizedState(tokenId);

        if (unfinalizedState != bytes32(0))
            revert ContentAssetErrors.UpdateIsNotFinalized(contentAssetStorageAddress, tokenId, unfinalizedState);

        assertionContract.createAssertion(assertionId, size, triplesNumber, chunksNumber);
        uss.setUnfinalizedState(tokenId, assertionId);
        uss.setIssuer(tokenId, msg.sender);

        sasProxy.createV1U1ServiceAgreementObject(
            agreementId,
            startTime,
            epochsNumber,
            epochLength,
            0, // tokenAmount, migrated from sasV1 during update finalization
            scoreFunctionIdAndProofWindowOffsetPerc[0],
            scoreFunctionIdAndProofWindowOffsetPerc[1]
        );

        if (updateTokenAmount != 0) serviceAgreementV1.addUpdateTokens(msg.sender, agreementId, updateTokenAmount);

        uint256 unfinalizedStateIndex = cas.getAssertionIdsLength(tokenId);
        sasProxy.setUpdateCommitsDeadline(
            keccak256(abi.encodePacked(agreementId, unfinalizedStateIndex)),
            block.timestamp + parametersStorage.updateCommitWindowDuration()
        );

        emit AssetStateUpdated(contentAssetStorageAddress, tokenId, unfinalizedStateIndex, updateTokenAmount);
    }

    function cancelAssetStateUpdate(uint256 tokenId) external onlyAssetOwner(tokenId) {
        ContentAssetStorage cas = contentAssetStorage;
        ServiceAgreementStorageProxy sasProxy = serviceAgreementStorageProxy;
        UnfinalizedStateStorage uss = unfinalizedStateStorage;

        address contentAssetStorageAddress = address(cas);

        bytes memory keyword = abi.encodePacked(contentAssetStorageAddress, cas.getAssertionIdByIndex(tokenId, 0));

        bytes32 agreementId = hashingProxy.callHashFunction(
            HASH_FUNCTION_ID,
            abi.encodePacked(contentAssetStorageAddress, tokenId, keyword)
        );

        uint256 startTime;
        uint16 epochsNumber;
        uint128 epochLength;
        (startTime, epochsNumber, epochLength, , ) = sasProxy.getAgreementData(agreementId);

        if (block.timestamp > startTime + epochsNumber * epochLength) revert ContentAssetErrors.AssetExpired(tokenId);

        bytes32 unfinalizedState = uss.getUnfinalizedState(tokenId);
        uint256 unfinalizedStateIndex = cas.getAssertionIdsLength(tokenId);

        if (unfinalizedState == bytes32(0)) {
            revert ContentAssetErrors.NoPendingUpdate(contentAssetStorageAddress, tokenId);
        } else if (
            block.timestamp <=
            sasProxy.getUpdateCommitsDeadline(keccak256(abi.encodePacked(agreementId, unfinalizedStateIndex)))
        ) {
            revert ContentAssetErrors.PendingUpdateFinalization(
                contentAssetStorageAddress,
                tokenId,
                unfinalizedStateIndex
            );
        }

        uint96 updateTokenAmount = sasProxy.getAgreementUpdateTokenAmount(agreementId);
        sasProxy.setAgreementUpdateTokenAmount(agreementId, 0);
        sasProxy.transferV1U1AgreementTokens(msg.sender, updateTokenAmount);

        uss.deleteIssuer(tokenId);
        uss.deleteUnfinalizedState(tokenId);

        emit AssetStateUpdateCanceled(
            contentAssetStorageAddress,
            tokenId,
            cas.getAssertionIdsLength(tokenId),
            updateTokenAmount
        );
    }

    function extendAssetStoringPeriod(
        uint256 tokenId,
        uint16 epochsNumber,
        uint96 tokenAmount
    ) external onlyAssetOwner(tokenId) {
        ContentAssetStorage cas = contentAssetStorage;
        ServiceAgreementV1 sasV1 = serviceAgreementV1;

        address contentAssetStorageAddress = address(cas);

        bytes32 unfinalizedState = unfinalizedStateStorage.getUnfinalizedState(tokenId);

        if (unfinalizedState != bytes32(0))
            revert ContentAssetErrors.UpdateIsNotFinalized(contentAssetStorageAddress, tokenId, unfinalizedState);

        bytes memory keyword = abi.encodePacked(contentAssetStorageAddress, cas.getAssertionIdByIndex(tokenId, 0));

        bytes32 agreementId = hashingProxy.callHashFunction(
            HASH_FUNCTION_ID,
            abi.encodePacked(contentAssetStorageAddress, tokenId, keyword)
        );

        uint256 startTime;
        uint16 oldEpochsNumber;
        uint128 epochLength;
        (startTime, oldEpochsNumber, epochLength, , ) = serviceAgreementStorageProxy.getAgreementData(agreementId);

        if (block.timestamp > startTime + oldEpochsNumber * epochLength) {
            revert ContentAssetErrors.AssetExpired(tokenId);
        }

        sasV1.extendStoringPeriod(msg.sender, agreementId, epochsNumber, tokenAmount);

        emit AssetStoringPeriodExtended(contentAssetStorageAddress, tokenId, epochsNumber, tokenAmount);
    }

    function increaseAssetTokenAmount(uint256 tokenId, uint96 tokenAmount) external onlyAssetOwner(tokenId) {
        ContentAssetStorage cas = contentAssetStorage;
        ServiceAgreementV1 sasV1 = serviceAgreementV1;

        address contentAssetStorageAddress = address(cas);

        bytes32 unfinalizedState = unfinalizedStateStorage.getUnfinalizedState(tokenId);

        if (unfinalizedState != bytes32(0))
            revert ContentAssetErrors.UpdateIsNotFinalized(contentAssetStorageAddress, tokenId, unfinalizedState);

        bytes memory keyword = abi.encodePacked(contentAssetStorageAddress, cas.getAssertionIdByIndex(tokenId, 0));

        bytes32 agreementId = hashingProxy.callHashFunction(
            HASH_FUNCTION_ID,
            abi.encodePacked(contentAssetStorageAddress, tokenId, keyword)
        );

        uint256 startTime;
        uint16 epochsNumber;
        uint128 epochLength;
        (startTime, epochsNumber, epochLength, , ) = serviceAgreementStorageProxy.getAgreementData(agreementId);

        if (block.timestamp > startTime + epochsNumber * epochLength) revert ContentAssetErrors.AssetExpired(tokenId);

        sasV1.addTokens(msg.sender, agreementId, tokenAmount);

        emit AssetPaymentIncreased(contentAssetStorageAddress, tokenId, tokenAmount);
    }

    function increaseAssetUpdateTokenAmount(uint256 tokenId, uint96 tokenAmount) external onlyAssetOwner(tokenId) {
        ContentAssetStorage cas = contentAssetStorage;
        ServiceAgreementV1 sasV1 = serviceAgreementV1;

        address contentAssetStorageAddress = address(cas);

        bytes32 unfinalizedState = unfinalizedStateStorage.getUnfinalizedState(tokenId);

        if (unfinalizedState == bytes32(0))
            revert ContentAssetErrors.NoPendingUpdate(contentAssetStorageAddress, tokenId);

        bytes memory keyword = abi.encodePacked(contentAssetStorageAddress, cas.getAssertionIdByIndex(tokenId, 0));

        bytes32 agreementId = hashingProxy.callHashFunction(
            HASH_FUNCTION_ID,
            abi.encodePacked(contentAssetStorageAddress, tokenId, keyword)
        );

        uint256 startTime;
        uint16 epochsNumber;
        uint128 epochLength;
        (startTime, epochsNumber, epochLength, , ) = serviceAgreementStorageProxy.getAgreementData(agreementId);

        if (block.timestamp > startTime + epochsNumber * epochLength) revert ContentAssetErrors.AssetExpired(tokenId);

        sasV1.addUpdateTokens(msg.sender, agreementId, tokenAmount);

        emit AssetUpdatePaymentIncreased(contentAssetStorageAddress, tokenId, tokenAmount);
    }

    function _createAsset(
        bytes32 assertionId,
        uint128 size,
        uint32 triplesNumber,
        uint96 chunksNumber,
        uint16 epochsNumber,
        uint96 tokenAmount,
        uint8 scoreFunctionId,
        bool immutable_
    ) internal virtual {
        ContentAssetStorage cas = contentAssetStorage;

        uint256 tokenId = cas.generateTokenId();
        cas.mint(msg.sender, tokenId);

        assertionContract.createAssertion(assertionId, size, triplesNumber, chunksNumber);
        cas.setAssertionIssuer(tokenId, assertionId, msg.sender);
        cas.setMutability(tokenId, immutable_);
        cas.pushAssertionId(tokenId, assertionId);

        address contentAssetStorageAddress = address(cas);

        serviceAgreementV1.createServiceAgreement(
            ServiceAgreementStructsV1.ServiceAgreementInputArgs({
                assetCreator: msg.sender,
                assetContract: contentAssetStorageAddress,
                tokenId: tokenId,
                keyword: abi.encodePacked(contentAssetStorageAddress, assertionId),
                hashFunctionId: HASH_FUNCTION_ID,
                epochsNumber: epochsNumber,
                tokenAmount: tokenAmount,
                scoreFunctionId: scoreFunctionId
            })
        );

        emit AssetMinted(contentAssetStorageAddress, tokenId, assertionId);
    }

    function _checkAssetOwner(uint256 tokenId) internal view virtual {
        require(msg.sender == contentAssetStorage.ownerOf(tokenId), "Only asset owner can use this fn");
    }

    function _checkMutability(uint256 tokenId) internal view virtual {
        require(contentAssetStorage.isMutable(tokenId), "Asset is immutable");
    }
}

contracts/CommitManagerV1.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.16;

import {HashingProxy} from "./HashingProxy.sol";
import {ScoringProxy} from "./ScoringProxy.sol";
import {Staking} from "./Staking.sol";
import {IdentityStorage} from "./storage/IdentityStorage.sol";
import {ParametersStorage} from "./storage/ParametersStorage.sol";
import {ProfileStorage} from "./storage/ProfileStorage.sol";
import {ServiceAgreementStorageProxy} from "./storage/ServiceAgreementStorageProxy.sol";
import {ShardingTableStorage} from "./storage/ShardingTableStorage.sol";
import {StakingStorage} from "./storage/StakingStorage.sol";
import {ContractStatus} from "./abstract/ContractStatus.sol";
import {Initializable} from "./interface/Initializable.sol";
import {Named} from "./interface/Named.sol";
import {Versioned} from "./interface/Versioned.sol";
import {ContentAssetErrors} from "./errors/assets/ContentAssetErrors.sol";
import {GeneralErrors} from "./errors/GeneralErrors.sol";
import {ServiceAgreementErrorsV1} from "./errors/ServiceAgreementErrorsV1.sol";
import {ServiceAgreementStructsV1} from "./structs/ServiceAgreementStructsV1.sol";

contract CommitManagerV1 is Named, Versioned, ContractStatus, Initializable {
    event CommitSubmitted(
        address indexed assetContract,
        uint256 indexed tokenId,
        bytes keyword,
        uint8 hashFunctionId,
        uint16 epoch,
        uint72 indexed identityId,
        uint40 score
    );

    string private constant _NAME = "CommitManagerV1";
    string private constant _VERSION = "1.0.0";

    bool[4] public reqs = [false, false, false, false];

    HashingProxy public hashingProxy;
    ScoringProxy public scoringProxy;
    Staking public stakingContract;
    IdentityStorage public identityStorage;
    ParametersStorage public parametersStorage;
    ProfileStorage public profileStorage;
    ServiceAgreementStorageProxy public serviceAgreementStorageProxy;
    ShardingTableStorage public shardingTableStorage;
    StakingStorage public stakingStorage;

    // solhint-disable-next-line no-empty-blocks
    constructor(address hubAddress) ContractStatus(hubAddress) {}

    function initialize() public onlyHubOwner {
        hashingProxy = HashingProxy(hub.getContractAddress("HashingProxy"));
        scoringProxy = ScoringProxy(hub.getContractAddress("ScoringProxy"));
        stakingContract = Staking(hub.getContractAddress("Staking"));
        identityStorage = IdentityStorage(hub.getContractAddress("IdentityStorage"));
        parametersStorage = ParametersStorage(hub.getContractAddress("ParametersStorage"));
        profileStorage = ProfileStorage(hub.getContractAddress("ProfileStorage"));
        serviceAgreementStorageProxy = ServiceAgreementStorageProxy(
            hub.getContractAddress("ServiceAgreementStorageProxy")
        );
        shardingTableStorage = ShardingTableStorage(hub.getContractAddress("ShardingTableStorage"));
        stakingStorage = StakingStorage(hub.getContractAddress("StakingStorage"));
    }

    function name() external pure virtual override returns (string memory) {
        return _NAME;
    }

    function version() external pure virtual override returns (string memory) {
        return _VERSION;
    }

    function isCommitWindowOpen(bytes32 agreementId, uint16 epoch) public view returns (bool) {
        ServiceAgreementStorageProxy sasProxy = serviceAgreementStorageProxy;
        uint256 startTime = sasProxy.getAgreementStartTime(agreementId);

        ParametersStorage params = parametersStorage;
        uint128 epochLength = sasProxy.getAgreementEpochLength(agreementId);

        if (startTime == 0) revert ServiceAgreementErrorsV1.ServiceAgreementDoesntExist(agreementId);
        if (epoch >= sasProxy.getAgreementEpochsNumber(agreementId))
            revert ServiceAgreementErrorsV1.ServiceAgreementHasBeenExpired(
                agreementId,
                startTime,
                sasProxy.getAgreementEpochsNumber(agreementId),
                epochLength
            );

        uint256 timeNow = block.timestamp;
        uint256 commitWindowDuration = (params.commitWindowDurationPerc() * epochLength) / 100;

        if (epoch == 0) {
            return timeNow < (startTime + commitWindowDuration);
        }

        return (timeNow >= (startTime + epochLength * epoch) &&
            timeNow < (startTime + epochLength * epoch + commitWindowDuration));
    }

    function getTopCommitSubmissions(
        bytes32 agreementId,
        uint16 epoch
    ) external view returns (ServiceAgreementStructsV1.CommitSubmission[] memory) {
        ServiceAgreementStorageProxy sasProxy = serviceAgreementStorageProxy;

        if (!sasProxy.serviceAgreementExists(agreementId))
            revert ServiceAgreementErrorsV1.ServiceAgreementDoesntExist(agreementId);
        if (epoch >= sasProxy.getAgreementEpochsNumber(agreementId))
            revert ServiceAgreementErrorsV1.ServiceAgreementHasBeenExpired(
                agreementId,
                sasProxy.getAgreementStartTime(agreementId),
                sasProxy.getAgreementEpochsNumber(agreementId),
                sasProxy.getAgreementEpochLength(agreementId)
            );

        uint32 r0 = parametersStorage.r0();

        ServiceAgreementStructsV1.CommitSubmission[]
            memory epochCommits = new ServiceAgreementStructsV1.CommitSubmission[](r0);

        bytes32 epochSubmissionsHead = sasProxy.getV1AgreementEpochSubmissionHead(agreementId, epoch);

        epochCommits[0] = sasProxy.getCommitSubmission(epochSubmissionsHead);

        bytes32 commitId;
        uint72 nextIdentityId = epochCommits[0].nextIdentityId;
        uint8 submissionsIdx = 1;
        while ((submissionsIdx < r0) && (nextIdentityId != 0)) {
            commitId = keccak256(abi.encodePacked(agreementId, epoch, nextIdentityId));
            epochCommits[submissionsIdx] = sasProxy.getCommitSubmission(commitId);

            nextIdentityId = epochCommits[submissionsIdx].nextIdentityId;

            unchecked {
                submissionsIdx++;
            }
        }

        return epochCommits;
    }

    function submitCommit(ServiceAgreementStructsV1.CommitInputArgs calldata args) external {
        ServiceAgreementStorageProxy sasProxy = serviceAgreementStorageProxy;

        bytes32 agreementId = hashingProxy.callHashFunction(
            args.hashFunctionId,
            abi.encodePacked(args.assetContract, args.tokenId, args.keyword)
        );

        if (!sasProxy.serviceAgreementExists(agreementId))
            revert ServiceAgreementErrorsV1.ServiceAgreementDoesntExist(agreementId);

        if (!reqs[0] && !isCommitWindowOpen(agreementId, args.epoch)) {
            uint128 epochLength = sasProxy.getAgreementEpochLength(agreementId);

            uint256 actualCommitWindowStart = (sasProxy.getAgreementStartTime(agreementId) + args.epoch * epochLength);

            revert ServiceAgreementErrorsV1.CommitWindowClosed(
                agreementId,
                args.epoch,
                actualCommitWindowStart,
                actualCommitWindowStart + (parametersStorage.commitWindowDurationPerc() * epochLength) / 100,
                block.timestamp
            );
        }

        uint72 identityId = identityStorage.getIdentityId(msg.sender);

        if (!reqs[1] && !shardingTableStorage.nodeExists(identityId)) {
            ProfileStorage ps = profileStorage;

            revert ServiceAgreementErrorsV1.NodeNotInShardingTable(
                identityId,
                ps.getNodeId(identityId),
                ps.getAsk(identityId),
                stakingStorage.totalStakes(identityId)
            );
        }

        uint40 score = scoringProxy.callScoreFunction(
            sasProxy.getAgreementScoreFunctionId(agreementId),
            args.hashFunctionId,
            profileStorage.getNodeId(identityId),
            args.keyword,
            stakingStorage.totalStakes(identityId)
        );

        _insertCommit(agreementId, args.epoch, identityId, 0, 0, score);

        emit CommitSubmitted(
            args.assetContract,
            args.tokenId,
            args.keyword,
            args.hashFunctionId,
            args.epoch,
            identityId,
            score
        );
    }

    function setReq(uint256 index, bool req) external onlyHubOwner {
        reqs[index] = req;
    }

    function _insertCommit(
        bytes32 agreementId,
        uint16 epoch,
        uint72 identityId,
        uint72 prevIdentityId,
        uint72 nextIdentityId,
        uint40 score
    ) internal virtual {
        ServiceAgreementStorageProxy sasProxy = serviceAgreementStorageProxy;

        bytes32 commitId = keccak256(abi.encodePacked(agreementId, epoch, identityId));

        if (!reqs[2] && sasProxy.commitSubmissionExists(commitId))
            revert ServiceAgreementErrorsV1.NodeAlreadySubmittedCommit(
                agreementId,
                epoch,
                identityId,
                profileStorage.getNodeId(identityId)
            );

        bytes32 refCommitId = sasProxy.getV1AgreementEpochSubmissionHead(agreementId, epoch);

        ParametersStorage params = parametersStorage;

        uint72 refCommitNextIdentityId = sasProxy.getCommitSubmissionNextIdentityId(refCommitId);
        uint32 r0 = params.r0();
        uint8 i;
        while ((score < sasProxy.getCommitSubmissionScore(refCommitId)) && (refCommitNextIdentityId != 0) && (i < r0)) {
            refCommitId = keccak256(abi.encodePacked(agreementId, epoch, refCommitNextIdentityId));

            refCommitNextIdentityId = sasProxy.getCommitSubmissionNextIdentityId(refCommitId);
            unchecked {
                i++;
            }
        }

        if (!reqs[3] && (i >= r0))
            revert ServiceAgreementErrorsV1.NodeNotAwarded(
                agreementId,
                epoch,
                identityId,
                profileStorage.getNodeId(identityId),
                i
            );

        sasProxy.createV1CommitSubmissionObject(commitId, identityId, prevIdentityId, nextIdentityId, score);

        ServiceAgreementStructsV1.CommitSubmission memory refCommit = sasProxy.getCommitSubmission(refCommitId);

        if ((i == 0) && (refCommit.identityId == 0)) {
            //  No head -> Setting new head
            sasProxy.setV1AgreementEpochSubmissionHead(agreementId, epoch, commitId);
        } else if ((i == 0) && (score <= refCommit.score)) {
            // There is a head with higher or equal score, add new commit on the right
            _linkCommits(agreementId, epoch, refCommit.identityId, identityId);
        } else if ((i == 0) && (score > refCommit.score)) {
            // There is a head with lower score, replace the head
            sasProxy.setV1AgreementEpochSubmissionHead(agreementId, epoch, commitId);
            _linkCommits(agreementId, epoch, identityId, refCommit.identityId);
        } else if (score > refCommit.score) {
            // [H] - head
            // [RC] - reference commit
            // [RC-] - commit before reference commit
            // [RC+] - commit after reference commit
            // [NC] - new commit
            // [] <-> [H] <-> [X] ... [RC-] <-> [RC] <-> [RC+] ... [C] <-> []
            // [] <-> [H] <-> [X] ... [RC-] <-(NL)-> [NC] <-(NL)-> [RC] <-> [RC+] ... [C] <-> []
            _linkCommits(agreementId, epoch, refCommit.prevIdentityId, identityId);
            _linkCommits(agreementId, epoch, identityId, refCommit.identityId);
        } else {
            // [] <-> [H] <-> [RC] <-> []
            // [] <-> [H] <-> [RC] <-(NL)-> [NC] <-> []
            _linkCommits(agreementId, epoch, refCommit.identityId, identityId);
        }
    }

    function _linkCommits(
        bytes32 agreementId,
        uint16 epoch,
        uint72 leftIdentityId,
        uint72 rightIdentityId
    ) internal virtual {
        ServiceAgreementStorageProxy sasProxy = serviceAgreementStorageProxy;

        sasProxy.setCommitSubmissionNextIdentityId(
            keccak256(abi.encodePacked(agreementId, epoch, leftIdentityId)), // leftCommitId
            rightIdentityId
        );

        sasProxy.setCommitSubmissionPrevIdentityId(
            keccak256(abi.encodePacked(agreementId, epoch, rightIdentityId)), // rightCommitId
            leftIdentityId
        );
    }
}

