    function getKey(
        uint72 identityId,
        bytes32 _key
    ) external view returns (uint256 purpose, uint256 keyType, bytes32 key);

    function getKeysByPurpose(uint72 identityId, uint256 _purpose) external view returns (bytes32[] memory keys);
}

