// SPDX-License-Identifier: MIT

pragma solidity ^0.8.16;

import {HashingProxy} from "./HashingProxy.sol";
import {Identity} from "./Identity.sol";
import {Shares} from "./Shares.sol";
import {IdentityStorage} from "./storage/IdentityStorage.sol";
import {ParametersStorage} from "./storage/ParametersStorage.sol";
import {ProfileStorage} from "./storage/ProfileStorage.sol";
import {Staking} from "./Staking.sol";
import {WhitelistStorage} from "./storage/WhitelistStorage.sol";
import {ContractStatus} from "./abstract/ContractStatus.sol";
import {Initializable} from "./interface/Initializable.sol";
import {Named} from "./interface/Named.sol";
import {Versioned} from "./interface/Versioned.sol";
import {UnorderedIndexableContractDynamicSetLib} from "./utils/UnorderedIndexableContractDynamicSet.sol";
import {ADMIN_KEY, OPERATIONAL_KEY} from "./constants/IdentityConstants.sol";

contract Profile is Named, Versioned, ContractStatus, Initializable {
    event ProfileCreated(uint72 indexed identityId, bytes nodeId);
    event ProfileDeleted(uint72 indexed identityId);
    event AskUpdated(uint72 indexed identityId, bytes nodeId, uint96 ask);

    string private constant _NAME = "Profile";
    string private constant _VERSION = "1.0.2";

    HashingProxy public hashingProxy;
    Identity public identityContract;
    Staking public stakingContract;
    IdentityStorage public identityStorage;
    ParametersStorage public parametersStorage;
    ProfileStorage public profileStorage;
    WhitelistStorage public whitelistStorage;

    // solhint-disable-next-line no-empty-blocks
    constructor(address hubAddress) ContractStatus(hubAddress) {}

    modifier onlyIdentityOwner(uint72 identityId) {
        _checkIdentityOwner(identityId);
        _;
    }

    modifier onlyAdmin(uint72 identityId) {
        _checkAdmin(identityId);
        _;
    }

    modifier onlyOperational(uint72 identityId) {
        _checkOperational(identityId);
        _;
    }

    modifier onlyWhitelisted() {
        _checkWhitelist();
        _;
    }

    function initialize() public onlyHubOwner {
        hashingProxy = HashingProxy(hub.getContractAddress("HashingProxy"));
        identityContract = Identity(hub.getContractAddress("Identity"));
        stakingContract = Staking(hub.getContractAddress("Staking"));
        identityStorage = IdentityStorage(hub.getContractAddress("IdentityStorage"));
        parametersStorage = ParametersStorage(hub.getContractAddress("ParametersStorage"));
        profileStorage = ProfileStorage(hub.getContractAddress("ProfileStorage"));
        whitelistStorage = WhitelistStorage(hub.getContractAddress("WhitelistStorage"));
    }

    function name() external pure virtual override returns (string memory) {
        return _NAME;
    }

    function version() external pure virtual override returns (string memory) {
        return _VERSION;
    }

    function createProfile(
        address adminWallet,
        bytes calldata nodeId,
        string calldata sharesTokenName,
        string calldata sharesTokenSymbol
    ) external onlyWhitelisted {
        IdentityStorage ids = identityStorage;
        ProfileStorage ps = profileStorage;

        require(ids.getIdentityId(msg.sender) == 0, "Identity already exists");
        require(nodeId.length != 0, "Node ID can't be empty");
        require(!ps.nodeIdsList(nodeId), "Node ID is already registered");
        require(
            keccak256(abi.encodePacked(sharesTokenName)) != keccak256(abi.encodePacked("")),
            "Token name cannot be empty"
        );
        require(
            keccak256(abi.encodePacked(sharesTokenSymbol)) != keccak256(abi.encodePacked("")),
            "Token symbol cannot be empty"
        );
        require(!ps.sharesNames(sharesTokenName), "Token name is already taken");
        require(!ps.sharesSymbols(sharesTokenSymbol), "Token symbol is already taken");

        uint72 identityId = identityContract.createIdentity(msg.sender, adminWallet);

        Shares sharesContract = new Shares(address(hub), sharesTokenName, sharesTokenSymbol);

        ps.createProfile(identityId, nodeId, address(sharesContract));
        _setAvailableNodeAddresses(identityId);

        emit ProfileCreated(identityId, nodeId);
    }

    function setAsk(uint72 identityId, uint96 ask) external onlyIdentityOwner(identityId) {
        require(ask != 0, "Ask cannot be 0");
        ProfileStorage ps = profileStorage;
        ps.setAsk(identityId, ask);

        emit AskUpdated(identityId, ps.getNodeId(identityId), ask);
    }

    // function deleteProfile(uint72 identityId) external onlyAdmin(identityId) {
    //     // TODO: add checks
    //     profileStorage.deleteProfile(identityId);
    //     identityContract.deleteIdentity(identityId);
    //
    //     emit ProfileDeleted(identityId);
    // }

    // function changeNodeId(uint72 identityId, bytes calldata nodeId) external onlyOperational(identityId) {
    //     require(nodeId.length != 0, "Node ID can't be empty");

    //     profileStorage.setNodeId(identityId, nodeId);
    // }

    // function addNewNodeIdHash(uint72 identityId, uint8 hashFunctionId) external onlyOperational(identityId) {
    //     HashingProxy hp = hashingProxy;
    //     require(hp.isHashFunction(hashFunctionId), "Hash function doesn't exist");

    //     profileStorage.setNodeAddress(
    //         identityId,
    //         hashFunctionId,
    //         hp.callHashFunction(hashFunctionId, profileStorage.getNodeId(identityId))
    //     );
    // }

    // TODO: Define where it can be called, change internal modifier
    function _setAvailableNodeAddresses(uint72 identityId) internal virtual {
        ProfileStorage ps = profileStorage;
        HashingProxy hp = hashingProxy;

        bytes memory nodeId = ps.getNodeId(identityId);
        bytes32 nodeAddress;

        UnorderedIndexableContractDynamicSetLib.Contract[] memory hashFunctions = hp.getAllHashFunctions();
        uint256 hashFunctionsNumber = hashFunctions.length;
        uint8 hashFunctionId;
        for (uint8 i; i < hashFunctionsNumber; ) {
            hashFunctionId = hashFunctions[i].id;
            nodeAddress = hp.callHashFunction(hashFunctionId, nodeId);
            ps.setNodeAddress(identityId, hashFunctionId, nodeAddress);
            unchecked {
                i++;
            }
        }
    }

    function stakeAccumulatedOperatorFee(uint72 identityId) external onlyAdmin(identityId) {
        ProfileStorage ps = profileStorage;

        uint96 accumulatedOperatorFee = ps.getAccumulatedOperatorFee(identityId);
        require(accumulatedOperatorFee != 0, "You have no operator fees");

        ps.setAccumulatedOperatorFee(identityId, 0);
        stakingContract.addStake(msg.sender, identityId, accumulatedOperatorFee);
    }

    function startAccumulatedOperatorFeeWithdrawal(uint72 identityId) external onlyAdmin(identityId) {
        ProfileStorage ps = profileStorage;

        uint96 accumulatedOperatorFee = ps.getAccumulatedOperatorFee(identityId);

        require(accumulatedOperatorFee != 0, "You have no operator fees");

        ps.setAccumulatedOperatorFee(identityId, 0);
        ps.setAccumulatedOperatorFeeWithdrawalAmount(
            identityId,
            ps.getAccumulatedOperatorFeeWithdrawalAmount(identityId) + accumulatedOperatorFee
        );
        ps.setAccumulatedOperatorFeeWithdrawalTimestamp(
            identityId,
            block.timestamp + parametersStorage.stakeWithdrawalDelay()
        );
    }

    function withdrawAccumulatedOperatorFee(uint72 identityId) external onlyAdmin(identityId) {
        ProfileStorage ps = profileStorage;

        uint96 withdrawalAmount = ps.getAccumulatedOperatorFeeWithdrawalAmount(identityId);

        require(withdrawalAmount != 0, "Withdrawal hasn't been initiated");
        require(
            ps.getAccumulatedOperatorFeeWithdrawalTimestamp(identityId) < block.timestamp,
            "Withdrawal period hasn't ended"
        );

        ps.setAccumulatedOperatorFeeWithdrawalAmount(identityId, 0);
        ps.setAccumulatedOperatorFeeWithdrawalTimestamp(identityId, 0);
        ps.transferAccumulatedOperatorFee(msg.sender, withdrawalAmount);
    }

    function _checkIdentityOwner(uint72 identityId) internal view virtual {
        require(
            identityStorage.keyHasPurpose(identityId, keccak256(abi.encodePacked(msg.sender)), ADMIN_KEY) ||
                identityStorage.keyHasPurpose(identityId, keccak256(abi.encodePacked(msg.sender)), OPERATIONAL_KEY),
            "Fn can be used only by id owner"
        );
    }

    function _checkAdmin(uint72 identityId) internal view virtual {
        require(
            identityStorage.keyHasPurpose(identityId, keccak256(abi.encodePacked(msg.sender)), ADMIN_KEY),
            "Admin function"
        );
    }

    function _checkOperational(uint72 identityId) internal view virtual {
        require(
            identityStorage.keyHasPurpose(identityId, keccak256(abi.encodePacked(msg.sender)), OPERATIONAL_KEY),
            "Fn can be called only by oper."
        );
    }

    function _checkWhitelist() internal view virtual {
        WhitelistStorage ws = whitelistStorage;
        if (ws.whitelistingEnabled()) {
            require(ws.whitelisted(msg.sender), "Address isn't whitelisted");
        }
    }
}

contracts/ProofManagerV1.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.16;

import {HashingProxy} from "./HashingProxy.sol";
import {Staking} from "./Staking.sol";
import {AssertionStorage} from "./storage/AssertionStorage.sol";
import {IdentityStorage} from "./storage/IdentityStorage.sol";
import {ParametersStorage} from "./storage/ParametersStorage.sol";
import {ProfileStorage} from "./storage/ProfileStorage.sol";
import {ServiceAgreementStorageProxy} from "./storage/ServiceAgreementStorageProxy.sol";
import {AbstractAsset} from "./abstract/AbstractAsset.sol";
import {ContractStatus} from "./abstract/ContractStatus.sol";
import {Initializable} from "./interface/Initializable.sol";
import {Named} from "./interface/Named.sol";
import {Versioned} from "./interface/Versioned.sol";
import {ServiceAgreementStructsV1} from "./structs/ServiceAgreementStructsV1.sol";
import {ContentAssetErrors} from "./errors/assets/ContentAssetErrors.sol";
import {GeneralErrors} from "./errors/GeneralErrors.sol";
import {ServiceAgreementErrorsV1} from "./errors/ServiceAgreementErrorsV1.sol";
import {MerkleProof} from "@openzeppelin/contracts/utils/cryptography/MerkleProof.sol";

contract ProofManagerV1 is Named, Versioned, ContractStatus, Initializable {
    event ProofSubmitted(
        address indexed assetContract,
        uint256 indexed tokenId,
        bytes keyword,
        uint8 hashFunctionId,
        uint16 epoch,
        uint72 indexed identityId
    );

    string private constant _NAME = "ProofManagerV1";
    string private constant _VERSION = "1.0.0";

    bool[4] public reqs = [false, false, false, false];

    HashingProxy public hashingProxy;
    Staking public stakingContract;
    AssertionStorage public assertionStorage;
    IdentityStorage public identityStorage;
    ParametersStorage public parametersStorage;
    ProfileStorage public profileStorage;
    ServiceAgreementStorageProxy public serviceAgreementStorageProxy;

    // solhint-disable-next-line no-empty-blocks
    constructor(address hubAddress) ContractStatus(hubAddress) {}

    function initialize() public onlyHubOwner {
        hashingProxy = HashingProxy(hub.getContractAddress("HashingProxy"));
        stakingContract = Staking(hub.getContractAddress("Staking"));
        assertionStorage = AssertionStorage(hub.getContractAddress("AssertionStorage"));
        identityStorage = IdentityStorage(hub.getContractAddress("IdentityStorage"));
        parametersStorage = ParametersStorage(hub.getContractAddress("ParametersStorage"));
        profileStorage = ProfileStorage(hub.getContractAddress("ProfileStorage"));
        serviceAgreementStorageProxy = ServiceAgreementStorageProxy(
            hub.getContractAddress("ServiceAgreementStorageProxy")
        );
    }

    function name() external pure virtual override returns (string memory) {
        return _NAME;
    }

    function version() external pure virtual override returns (string memory) {
        return _VERSION;
    }

    function isProofWindowOpen(bytes32 agreementId, uint16 epoch) public view returns (bool) {
        ServiceAgreementStorageProxy sasProxy = serviceAgreementStorageProxy;
        uint256 startTime = sasProxy.getAgreementStartTime(agreementId);

        if (startTime == 0) revert ServiceAgreementErrorsV1.ServiceAgreementDoesntExist(agreementId);
        if (epoch >= sasProxy.getAgreementEpochsNumber(agreementId))
            revert ServiceAgreementErrorsV1.ServiceAgreementHasBeenExpired(
                agreementId,
                startTime,
                sasProxy.getAgreementEpochsNumber(agreementId),
                sasProxy.getAgreementEpochLength(agreementId)
            );

        uint256 timeNow = block.timestamp;
        uint128 epochLength = sasProxy.getAgreementEpochLength(agreementId);
        uint8 proofWindowOffsetPerc = sasProxy.getAgreementProofWindowOffsetPerc(agreementId);

        uint256 proofWindowOffset = (epochLength * proofWindowOffsetPerc) / 100;
        uint256 proofWindowDuration = (epochLength * parametersStorage.proofWindowDurationPerc()) / 100;

        return (timeNow >= (startTime + epochLength * epoch + proofWindowOffset) &&
            timeNow < (startTime + epochLength * epoch + proofWindowOffset + proofWindowDuration));
    }

    function getChallenge(
        address sender,
        address assetContract,
        uint256 tokenId,
        uint16 epoch
    ) public view returns (bytes32 assertionId, uint256 challenge) {
        uint72 identityId = identityStorage.getIdentityId(sender);

        AbstractAsset generalAssetInterface = AbstractAsset(assetContract);
        assertionId = generalAssetInterface.getLatestAssertionId(tokenId);

        uint256 assertionChunksNumber = assertionStorage.getAssertionChunksNumber(assertionId);

        // blockchash() function only works for last 256 blocks (25.6 min window in case of 6s block time)
        // TODO: figure out how to achieve randomness
        return (assertionId, uint256(sha256(abi.encodePacked(epoch, identityId))) % assertionChunksNumber);
    }

    function sendProof(ServiceAgreementStructsV1.ProofInputArgs calldata args) external {
        ServiceAgreementStorageProxy sasProxy = serviceAgreementStorageProxy;

        bytes32 agreementId = hashingProxy.callHashFunction(
            args.hashFunctionId,
            abi.encodePacked(args.assetContract, args.tokenId, args.keyword)
        );

        if (!sasProxy.serviceAgreementExists(agreementId))
            revert ServiceAgreementErrorsV1.ServiceAgreementDoesntExist(agreementId);

        if (!reqs[0] && !isProofWindowOpen(agreementId, args.epoch)) {
            uint128 epochLength = sasProxy.getAgreementEpochLength(agreementId);

            uint256 actualCommitWindowStart = (sasProxy.getAgreementStartTime(agreementId) + args.epoch * epochLength);

            revert ServiceAgreementErrorsV1.ProofWindowClosed(
                agreementId,
                args.epoch,
                actualCommitWindowStart,
                actualCommitWindowStart + (parametersStorage.commitWindowDurationPerc() * epochLength) / 100,
                block.timestamp
            );
        }

        IdentityStorage ids = identityStorage;

        uint72 identityId = ids.getIdentityId(msg.sender);

        if (
            !reqs[1] &&
            (sasProxy.getCommitSubmissionScore(keccak256(abi.encodePacked(agreementId, args.epoch, identityId))) == 0)
        )
            revert ServiceAgreementErrorsV1.NodeAlreadyRewarded(
                agreementId,
                args.epoch,
                identityId,
                profileStorage.getNodeId(identityId)
            );

        bytes32 nextCommitId = sasProxy.getV1AgreementEpochSubmissionHead(agreementId, args.epoch);
        uint32 r0 = parametersStorage.r0();
        uint8 i;
        while ((identityId != sasProxy.getCommitSubmissionIdentityId(nextCommitId)) && (i < r0)) {
            nextCommitId = keccak256(
                abi.encodePacked(agreementId, args.epoch, sasProxy.getCommitSubmissionNextIdentityId(nextCommitId))
            );
            unchecked {
                i++;
            }
        }

        if (!reqs[2] && (i >= r0))
            revert ServiceAgreementErrorsV1.NodeNotAwarded(
                agreementId,
                args.epoch,
                identityId,
                profileStorage.getNodeId(identityId),
                i
            );

        bytes32 merkleRoot;
        uint256 challenge;
        (merkleRoot, challenge) = getChallenge(msg.sender, args.assetContract, args.tokenId, args.epoch);

        if (
            !reqs[3] &&
            !MerkleProof.verify(args.proof, merkleRoot, keccak256(abi.encodePacked(args.chunkHash, challenge)))
        )
            revert ServiceAgreementErrorsV1.WrongMerkleProof(
                agreementId,
                args.epoch,
                identityId,
                profileStorage.getNodeId(identityId),
                args.proof,
                merkleRoot,
                args.chunkHash,
                challenge
            );

        emit ProofSubmitted(
            args.assetContract,
            args.tokenId,
            args.keyword,
            args.hashFunctionId,
            args.epoch,
            identityId
        );

        uint96 reward = (sasProxy.getAgreementTokenAmount(agreementId) /
            (sasProxy.getAgreementEpochsNumber(agreementId) - args.epoch + 1) /
            (r0 - sasProxy.getAgreementRewardedNodesNumber(agreementId, args.epoch)));

        stakingContract.addReward(agreementId, identityId, reward);
        sasProxy.setAgreementTokenAmount(agreementId, sasProxy.getAgreementTokenAmount(agreementId) - reward);
        sasProxy.incrementAgreementRewardedNodesNumber(agreementId, args.epoch);

        // To make sure that node already received reward
        sasProxy.setCommitSubmissionScore(keccak256(abi.encodePacked(agreementId, args.epoch, identityId)), 0);
    }

    function setReq(uint256 index, bool req) external onlyHubOwner {
        reqs[index] = req;
    }
}

contracts/ProofManagerV1U1.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.16;

import {HashingProxy} from "./HashingProxy.sol";
import {Staking} from "./Staking.sol";
import {AssertionStorage} from "./storage/AssertionStorage.sol";
import {IdentityStorage} from "./storage/IdentityStorage.sol";
import {ParametersStorage} from "./storage/ParametersStorage.sol";
import {ProfileStorage} from "./storage/ProfileStorage.sol";
import {ServiceAgreementStorageProxy} from "./storage/ServiceAgreementStorageProxy.sol";
import {AbstractAsset} from "./abstract/AbstractAsset.sol";
import {ContractStatus} from "./abstract/ContractStatus.sol";
import {Initializable} from "./interface/Initializable.sol";
import {Named} from "./interface/Named.sol";
import {Versioned} from "./interface/Versioned.sol";
import {ServiceAgreementStructsV1} from "./structs/ServiceAgreementStructsV1.sol";
import {ContentAssetErrors} from "./errors/assets/ContentAssetErrors.sol";
import {GeneralErrors} from "./errors/GeneralErrors.sol";
import {ServiceAgreementErrorsV1U1} from "./errors/ServiceAgreementErrorsV1U1.sol";
import {MerkleProof} from "@openzeppelin/contracts/utils/cryptography/MerkleProof.sol";

contract ProofManagerV1U1 is Named, Versioned, ContractStatus, Initializable {
    event ProofSubmitted(
        address indexed assetContract,
        uint256 indexed tokenId,
        bytes keyword,
        uint8 hashFunctionId,
        uint16 epoch,
        uint256 stateIndex,
        uint72 indexed identityId
    );

    string private constant _NAME = "ProofManagerV1U1";
    string private constant _VERSION = "1.0.0";

    bool[4] public reqs = [false, false, false, false];

    HashingProxy public hashingProxy;
    Staking public stakingContract;
    AssertionStorage public assertionStorage;
    IdentityStorage public identityStorage;
    ParametersStorage public parametersStorage;
    ProfileStorage public profileStorage;
    ServiceAgreementStorageProxy public serviceAgreementStorageProxy;

    // solhint-disable-next-line no-empty-blocks
    constructor(address hubAddress) ContractStatus(hubAddress) {}

    function initialize() public onlyHubOwner {
        hashingProxy = HashingProxy(hub.getContractAddress("HashingProxy"));
        stakingContract = Staking(hub.getContractAddress("Staking"));
        assertionStorage = AssertionStorage(hub.getContractAddress("AssertionStorage"));
        identityStorage = IdentityStorage(hub.getContractAddress("IdentityStorage"));
        parametersStorage = ParametersStorage(hub.getContractAddress("ParametersStorage"));
        profileStorage = ProfileStorage(hub.getContractAddress("ProfileStorage"));
        serviceAgreementStorageProxy = ServiceAgreementStorageProxy(
            hub.getContractAddress("ServiceAgreementStorageProxy")
        );
    }

    function name() external pure virtual override returns (string memory) {
        return _NAME;
    }

    function version() external pure virtual override returns (string memory) {
        return _VERSION;
    }

    function isProofWindowOpen(bytes32 agreementId, uint16 epoch) public view returns (bool) {
        ServiceAgreementStorageProxy sasProxy = serviceAgreementStorageProxy;
        uint256 startTime = sasProxy.getAgreementStartTime(agreementId);

        if (startTime == 0) revert ServiceAgreementErrorsV1U1.ServiceAgreementDoesntExist(agreementId);
        if (epoch >= sasProxy.getAgreementEpochsNumber(agreementId))
            revert ServiceAgreementErrorsV1U1.ServiceAgreementHasBeenExpired(
                agreementId,
                startTime,
                sasProxy.getAgreementEpochsNumber(agreementId),
                sasProxy.getAgreementEpochLength(agreementId)
            );

        uint256 timeNow = block.timestamp;
        uint128 epochLength = sasProxy.getAgreementEpochLength(agreementId);
        uint8 proofWindowOffsetPerc = sasProxy.getAgreementProofWindowOffsetPerc(agreementId);

        uint256 proofWindowOffset = (epochLength * proofWindowOffsetPerc) / 100;
        uint256 proofWindowDuration = (epochLength * parametersStorage.proofWindowDurationPerc()) / 100;

        return (timeNow >= (startTime + epochLength * epoch + proofWindowOffset) &&
            timeNow < (startTime + epochLength * epoch + proofWindowOffset + proofWindowDuration));
    }

    function getChallenge(
        address assetContract,
        uint256 tokenId,
        uint16 epoch
    ) public view returns (bytes32 assertionId, uint256 challenge) {
        return _getChallenge(msg.sender, assetContract, tokenId, epoch);
    }

    function sendProof(ServiceAgreementStructsV1.ProofInputArgs calldata args) external {
        ServiceAgreementStorageProxy sasProxy = serviceAgreementStorageProxy;

        bytes32 agreementId = hashingProxy.callHashFunction(
            args.hashFunctionId,
            abi.encodePacked(args.assetContract, args.tokenId, args.keyword)
        );

        if (!sasProxy.serviceAgreementExists(agreementId))
            revert ServiceAgreementErrorsV1U1.ServiceAgreementDoesntExist(agreementId);

        uint256 latestFinalizedStateIndex = AbstractAsset(args.assetContract).getAssertionIdsLength(args.tokenId) - 1;

        if (!reqs[0] && !isProofWindowOpen(agreementId, args.epoch)) {
            uint128 epochLength = sasProxy.getAgreementEpochLength(agreementId);

            uint256 actualProofWindowStart = (sasProxy.getAgreementStartTime(agreementId) +
                args.epoch *
                epochLength +
                (sasProxy.getAgreementProofWindowOffsetPerc(agreementId) * epochLength) /
                100);

            revert ServiceAgreementErrorsV1U1.ProofWindowClosed(
                agreementId,
                args.epoch,
                latestFinalizedStateIndex,
                actualProofWindowStart,
                actualProofWindowStart + (parametersStorage.proofWindowDurationPerc() * epochLength) / 100,
                block.timestamp
            );
        }

        IdentityStorage ids = identityStorage;

        uint72 identityId = ids.getIdentityId(msg.sender);
        bytes32 commitId = keccak256(abi.encodePacked(agreementId, args.epoch, latestFinalizedStateIndex, identityId));

        if (!reqs[1] && (sasProxy.getCommitSubmissionScore(commitId) == 0))
            revert ServiceAgreementErrorsV1U1.NodeAlreadyRewarded(
                agreementId,
                args.epoch,
                latestFinalizedStateIndex,
                identityId,
                profileStorage.getNodeId(identityId)
            );

        bytes32 nextCommitId = sasProxy.getV1U1AgreementEpochSubmissionHead(
            agreementId,
            args.epoch,
            latestFinalizedStateIndex
        );
        uint32 r0 = parametersStorage.r0();
        uint8 i;
        while ((identityId != sasProxy.getCommitSubmissionIdentityId(nextCommitId)) && (i < r0)) {
            nextCommitId = keccak256(
                abi.encodePacked(
                    agreementId,
                    args.epoch,
                    latestFinalizedStateIndex,
                    sasProxy.getCommitSubmissionNextIdentityId(nextCommitId)
                )
            );
            unchecked {
                i++;
            }
        }

        if (!reqs[2] && (i >= r0))
            revert ServiceAgreementErrorsV1U1.NodeNotAwarded(
                agreementId,
                args.epoch,
                latestFinalizedStateIndex,
                identityId,
                profileStorage.getNodeId(identityId),
                i
            );

        bytes32 merkleRoot;
        uint256 challenge;
        (merkleRoot, challenge) = _getChallenge(msg.sender, args.assetContract, args.tokenId, args.epoch);

        if (
            !reqs[3] &&
            !MerkleProof.verify(args.proof, merkleRoot, keccak256(abi.encodePacked(args.chunkHash, challenge)))
        )
            revert ServiceAgreementErrorsV1U1.WrongMerkleProof(
                agreementId,
                args.epoch,
                latestFinalizedStateIndex,
                identityId,
                profileStorage.getNodeId(identityId),
                args.proof,
                merkleRoot,
                args.chunkHash,
                challenge
            );

        emit ProofSubmitted(
            args.assetContract,
            args.tokenId,
            args.keyword,
            args.hashFunctionId,
            args.epoch,
            latestFinalizedStateIndex,
            identityId
        );

        uint96 reward = (sasProxy.getAgreementTokenAmount(agreementId) /
            (sasProxy.getAgreementEpochsNumber(agreementId) - args.epoch + 1) /
            (r0 - sasProxy.getAgreementRewardedNodesNumber(agreementId, args.epoch)));

        stakingContract.addReward(agreementId, identityId, reward);
        sasProxy.setAgreementTokenAmount(agreementId, sasProxy.getAgreementTokenAmount(agreementId) - reward);
        sasProxy.incrementAgreementRewardedNodesNumber(agreementId, args.epoch);

        // To make sure that node already received reward
        sasProxy.setCommitSubmissionScore(commitId, 0);
    }

    function setReq(uint256 index, bool req) external onlyHubOwner {
        reqs[index] = req;
    }

    function _getChallenge(
        address sender,
        address assetContract,
        uint256 tokenId,
        uint16 epoch
    ) internal view returns (bytes32 assertionId, uint256 challenge) {
        uint72 identityId = identityStorage.getIdentityId(sender);

        AbstractAsset generalAssetInterface = AbstractAsset(assetContract);
        bytes32 latestFinalizedState = generalAssetInterface.getLatestAssertionId(tokenId);

        uint256 assertionChunksNumber = assertionStorage.getAssertionChunksNumber(latestFinalizedState);

        // blockchash() function only works for last 256 blocks (25.6 min window in case of 6s block time)
        // TODO: figure out how to achieve randomness
        return (latestFinalizedState, uint256(sha256(abi.encodePacked(epoch, identityId))) % assertionChunksNumber);
    }
}

contracts/scoring/log2pldsf.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.16;

import {HashingProxy} from "../HashingProxy.sol";
import {ParametersStorage} from "../storage/ParametersStorage.sol";
import {HubDependent} from "../abstract/HubDependent.sol";
import {Indexable} from "../interface/Indexable.sol";
import {Initializable} from "../interface/Initializable.sol";
import {IScoreFunction} from "../interface/IScoreFunction.sol";
import {Named} from "../interface/Named.sol";
import {PRBMathUD60x18} from "@prb/math/contracts/PRBMathUD60x18.sol";

// Logarithmic Polynomial Long Division Score Function
contract Log2PLDSF is IScoreFunction, Indexable, Named, HubDependent, Initializable {
    using PRBMathUD60x18 for uint256;

    uint8 private constant _ID = 1;
    string private constant _NAME = "Log2PLDSF";

    HashingProxy public hashingProxy;
    ParametersStorage public parametersStorage;

    uint256 public distanceMappingCoefficient;
    uint96 public stakeRangeMax;

    uint32 public multiplier;
    uint32 public logArgumentConstant;
    uint32 public a;
    uint32 public stakeExponent;
    uint32 public b;
    uint32 public c;
    uint32 public distanceExponent;
    uint32 public d;

    constructor(address hubAddress) HubDependent(hubAddress) {
        distanceMappingCoefficient = type(uint256).max / 1_000;
        stakeRangeMax = 200_000;

        multiplier = 10000;
        logArgumentConstant = 1;
        a = 1;
        stakeExponent = 1;
        b = 0;
        c = 1;
        distanceExponent = 2;
        d = 1;
    }

    function initialize() public onlyHubOwner {
        hashingProxy = HashingProxy(hub.getContractAddress("HashingProxy"));
        parametersStorage = ParametersStorage(hub.getContractAddress("ParametersStorage"));
    }

    function id() external pure virtual override returns (uint8) {
        return _ID;
    }

    function name() external pure virtual override returns (string memory) {
        return _NAME;
    }

    function calculateScore(uint256 distance, uint96 stake) external view returns (uint40) {
        uint256 mappedDistance = distance / distanceMappingCoefficient;
        uint96 mappedStake = stake / (parametersStorage.maximumStake() / stakeRangeMax);

        uint64 coefficient = 1 ether;

        return
            uint40(
                (multiplier *
                    (logArgumentConstant *
                        coefficient +
                        (coefficient * (a * (mappedStake ** stakeExponent) + b)) /
                        (c * (mappedDistance ** distanceExponent) + d)).log2()) / coefficient
            );
    }

    function calculateDistance(
        uint8 hashFunctionId,
        bytes calldata nodeId,
        bytes calldata keyword
    ) external view returns (uint256) {
        HashingProxy hp = hashingProxy;
        bytes32 nodeIdHash = hp.callHashFunction(hashFunctionId, nodeId);
        bytes32 keywordHash = hp.callHashFunction(hashFunctionId, keyword);

        return uint256(nodeIdHash ^ keywordHash);
    }

    function getParameters()
        external
        view
        returns (uint256 distanceMapCoefficient, uint96 stakeMapCoefficient, uint32[8] memory formulaCoefficients)
    {
        return (
            distanceMappingCoefficient,
            (parametersStorage.maximumStake() / stakeRangeMax),
            [multiplier, logArgumentConstant, a, stakeExponent, b, c, distanceExponent, d]
        );
    }

    function setDistanceMappingCoefficient(uint256 distanceRangeMax) external onlyHubOwner {
        distanceMappingCoefficient = type(uint256).max / distanceRangeMax;
    }

    function setStakeRangeMax(uint96 stakeRangeMax_) external onlyHubOwner {
        stakeRangeMax = stakeRangeMax_;
    }

    function setMultiplier(uint32 multiplier_) external onlyHubOwner {
        multiplier = multiplier_;
    }

    function setLogArgumentConstant(uint32 logArgumentConstant_) external onlyHubOwner {
        logArgumentConstant = logArgumentConstant_;
    }

    function setA(uint32 a_) external onlyHubOwner {
        a = a_;
    }

    function setStakeExponent(uint32 stakeExponent_) external onlyHubOwner {
        stakeExponent = stakeExponent_;
    }

    function setB(uint32 b_) external onlyHubOwner {
        b = b_;
    }

    function setC(uint32 c_) external onlyHubOwner {
        c = c_;
    }

    function setDistanceExponent(uint32 distanceExponent_) external onlyHubOwner {
        distanceExponent = distanceExponent_;
    }

    function setD(uint32 d_) external onlyHubOwner {
        d = d_;
    }
}

contracts/ScoringProxy.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.16;

import {ContractStatus} from "./abstract/ContractStatus.sol";
import {IScoreFunction} from "./interface/IScoreFunction.sol";
import {Named} from "./interface/Named.sol";
import {Versioned} from "./interface/Versioned.sol";
import {UnorderedIndexableContractDynamicSetLib} from "./utils/UnorderedIndexableContractDynamicSet.sol";

contract ScoringProxy is Named, Versioned, ContractStatus {
    using UnorderedIndexableContractDynamicSetLib for UnorderedIndexableContractDynamicSetLib.Set;

    event NewScoringFunctionContract(uint8 indexed scoreFunctionId, address newContractAddress);
    event ScoringFunctionContractUpdated(uint8 indexed scoreFunctionId, address newContractAddress);

    string private constant _NAME = "ScoringProxy";
    string private constant _VERSION = "1.0.1";

    UnorderedIndexableContractDynamicSetLib.Set internal scoreFunctionSet;

    // solhint-disable-next-line no-empty-blocks
    constructor(address hubAddress) ContractStatus(hubAddress) {}

    function name() external pure virtual override returns (string memory) {
        return _NAME;
    }

    function version() external pure virtual override returns (string memory) {
        return _VERSION;
    }

    function setContractAddress(uint8 scoreFunctionId, address scoringContractAddress) external onlyHubOwner {
        if (scoreFunctionSet.exists(scoreFunctionId)) {
            emit ScoringFunctionContractUpdated(scoreFunctionId, scoringContractAddress);
            scoreFunctionSet.update(scoreFunctionId, scoringContractAddress);
        } else {
            emit NewScoringFunctionContract(scoreFunctionId, scoringContractAddress);
            scoreFunctionSet.append(scoreFunctionId, scoringContractAddress);
        }
    }

    function removeContract(uint8 scoreFunctionId) external onlyHubOwner {
        scoreFunctionSet.remove(scoreFunctionId);
    }

    function callScoreFunction(
        uint8 scoreFunctionId,
        uint8 hashFunctionId,
        bytes calldata nodeId,
        bytes calldata keyword,
        uint96 stake
    ) external view returns (uint40) {
        IScoreFunction scoringFunction = IScoreFunction(scoreFunctionSet.get(scoreFunctionId).addr);
        uint256 distance = scoringFunction.calculateDistance(hashFunctionId, nodeId, keyword);
        return scoringFunction.calculateScore(distance, stake);
    }

    function getScoreFunctionName(uint8 scoreFunctionId) external view returns (string memory) {
        return Named(scoreFunctionSet.get(scoreFunctionId).addr).name();
    }

    function getScoreFunctionContractAddress(uint8 scoreFunctionId) external view returns (address) {
        return scoreFunctionSet.get(scoreFunctionId).addr;
    }

    function getAllScoreFunctions() external view returns (UnorderedIndexableContractDynamicSetLib.Contract[] memory) {
        return scoreFunctionSet.getAll();
    }

    function isScoreFunction(uint8 scoreFunctionId) external view returns (bool) {
        return scoreFunctionSet.exists(scoreFunctionId);
    }
}

contracts/ServiceAgreementV1.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.16;

import {CommitManagerV1} from "./CommitManagerV1.sol";
import {CommitManagerV1U1} from "./CommitManagerV1U1.sol";
import {HashingProxy} from "./HashingProxy.sol";
import {ProofManagerV1} from "./ProofManagerV1.sol";
import {ProofManagerV1U1} from "./ProofManagerV1U1.sol";
import {ScoringProxy} from "./ScoringProxy.sol";
import {ParametersStorage} from "./storage/ParametersStorage.sol";
import {ServiceAgreementStorageProxy} from "./storage/ServiceAgreementStorageProxy.sol";
import {ContractStatus} from "./abstract/ContractStatus.sol";
import {Initializable} from "./interface/Initializable.sol";
import {Named} from "./interface/Named.sol";
import {Versioned} from "./interface/Versioned.sol";
import {ServiceAgreementStructsV1} from "./structs/ServiceAgreementStructsV1.sol";
import {GeneralErrors} from "./errors/GeneralErrors.sol";
import {ServiceAgreementErrorsV1U1} from "./errors/ServiceAgreementErrorsV1U1.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";

contract ServiceAgreementV1 is Named, Versioned, ContractStatus, Initializable {
    event ServiceAgreementV1Created(
        address indexed assetContract,
        uint256 indexed tokenId,
        bytes keyword,
        uint8 hashFunctionId,
        uint256 startTime,
        uint16 epochsNumber,
        uint128 epochLength,
        uint96 tokenAmount
    );
    event ServiceAgreementV1Terminated(bytes32 indexed agreementId);
    event ServiceAgreementV1Extended(bytes32 indexed agreementId, uint16 epochsNumber);
    event ServiceAgreementV1RewardRaised(bytes32 indexed agreementId, uint96 tokenAmount);
    event ServiceAgreementV1UpdateRewardRaised(bytes32 indexed agreementId, uint96 updateTokenAmount);

    string private constant _NAME = "ServiceAgreementV1";
    string private constant _VERSION = "1.1.1";

    CommitManagerV1 public commitManagerV1;
    CommitManagerV1U1 public commitManagerV1U1;
    ProofManagerV1 public proofManagerV1;
    ProofManagerV1U1 public proofManagerV1U1;
    HashingProxy public hashingProxy;
    ScoringProxy public scoringProxy;
    ParametersStorage public parametersStorage;
    ServiceAgreementStorageProxy public serviceAgreementStorageProxy;
    IERC20 public tokenContract;

    error ScoreError();

    // solhint-disable-next-line no-empty-blocks
    constructor(address hubAddress) ContractStatus(hubAddress) {}

    function initialize() public onlyHubOwner {
        commitManagerV1 = CommitManagerV1(hub.getContractAddress("CommitManagerV1"));
        commitManagerV1U1 = CommitManagerV1U1(hub.getContractAddress("CommitManagerV1U1"));
        proofManagerV1 = ProofManagerV1(hub.getContractAddress("ProofManagerV1"));
        proofManagerV1U1 = ProofManagerV1U1(hub.getContractAddress("ProofManagerV1U1"));
        hashingProxy = HashingProxy(hub.getContractAddress("HashingProxy"));
        scoringProxy = ScoringProxy(hub.getContractAddress("ScoringProxy"));
        parametersStorage = ParametersStorage(hub.getContractAddress("ParametersStorage"));
        serviceAgreementStorageProxy = ServiceAgreementStorageProxy(
            hub.getContractAddress("ServiceAgreementStorageProxy")
        );
        tokenContract = IERC20(hub.getContractAddress("Token"));
    }

    function name() external pure virtual override returns (string memory) {
        return _NAME;
    }

    function version() external pure virtual override returns (string memory) {
        return _VERSION;
    }

    function createServiceAgreement(
        ServiceAgreementStructsV1.ServiceAgreementInputArgs calldata args
    ) external onlyContracts {
        if (args.epochsNumber == 0) revert ServiceAgreementErrorsV1U1.ZeroEpochsNumber();
        if (args.tokenAmount == 0) revert ServiceAgreementErrorsV1U1.ZeroTokenAmount();
        if (!scoringProxy.isScoreFunction(args.scoreFunctionId))
            revert ServiceAgreementErrorsV1U1.ScoreFunctionDoesntExist(args.scoreFunctionId);

        bytes32 agreementId = hashingProxy.callHashFunction(
            args.hashFunctionId,
            abi.encodePacked(args.assetContract, args.tokenId, args.keyword)
        );

        ServiceAgreementStorageProxy sasProxy = serviceAgreementStorageProxy;
        ParametersStorage params = parametersStorage;

        sasProxy.createV1ServiceAgreementObject(
            agreementId,
            args.epochsNumber,
            params.epochLength(),
            args.tokenAmount,
            args.scoreFunctionId,
            params.minProofWindowOffsetPerc() +
                _generatePseudorandomUint8(
                    args.assetCreator,
                    params.maxProofWindowOffsetPerc() - params.minProofWindowOffsetPerc() + 1
                )
        );

        IERC20 tknc = tokenContract;
        if (tknc.allowance(args.assetCreator, address(this)) < args.tokenAmount)
            revert ServiceAgreementErrorsV1U1.TooLowAllowance(tknc.allowance(args.assetCreator, address(this)));
        if (tknc.balanceOf(args.assetCreator) < args.tokenAmount)
            revert ServiceAgreementErrorsV1U1.TooLowBalance(tknc.balanceOf(args.assetCreator));

        tknc.transferFrom(args.assetCreator, sasProxy.agreementV1StorageAddress(), args.tokenAmount);

        emit ServiceAgreementV1Created(
            args.assetContract,
            args.tokenId,
            args.keyword,
            args.hashFunctionId,
            block.timestamp,
            args.epochsNumber,
            params.epochLength(),
            args.tokenAmount
        );
    }

    function terminateAgreement(address assetOwner, bytes32 agreementId) external onlyContracts {
        ServiceAgreementStorageProxy sasProxy = serviceAgreementStorageProxy;

        uint96 agreementBalance = sasProxy.getAgreementTokenAmount(agreementId);

        sasProxy.setAgreementTokenAmount(agreementId, 0);
        sasProxy.transferAgreementTokens(agreementId, assetOwner, agreementBalance);
        sasProxy.deleteServiceAgreementObject(agreementId);

        emit ServiceAgreementV1Terminated(agreementId);
    }

    function extendStoringPeriod(
        address assetOwner,
        bytes32 agreementId,
        uint16 epochsNumber,
        uint96 tokenAmount
    ) external onlyContracts {
        if (epochsNumber == 0) revert ServiceAgreementErrorsV1U1.ZeroEpochsNumber();

        ServiceAgreementStorageProxy sasProxy = serviceAgreementStorageProxy;

        sasProxy.setAgreementEpochsNumber(agreementId, sasProxy.getAgreementEpochsNumber(agreementId) + epochsNumber);
        sasProxy.setAgreementTokenAmount(agreementId, sasProxy.getAgreementTokenAmount(agreementId) + tokenAmount);

        if (sasProxy.agreementV1Exists(agreementId)) {
            _addTokens(assetOwner, sasProxy.agreementV1StorageAddress(), tokenAmount);
        } else {
            _addTokens(assetOwner, sasProxy.agreementV1U1StorageAddress(), tokenAmount);
        }

        emit ServiceAgreementV1Extended(agreementId, epochsNumber);
    }

    function addTokens(address assetOwner, bytes32 agreementId, uint96 tokenAmount) external onlyContracts {
        ServiceAgreementStorageProxy sasProxy = serviceAgreementStorageProxy;

        sasProxy.setAgreementTokenAmount(agreementId, sasProxy.getAgreementTokenAmount(agreementId) + tokenAmount);

        if (sasProxy.agreementV1Exists(agreementId)) {
            _addTokens(assetOwner, sasProxy.agreementV1StorageAddress(), tokenAmount);
        } else {
            _addTokens(assetOwner, sasProxy.agreementV1U1StorageAddress(), tokenAmount);
        }

        emit ServiceAgreementV1RewardRaised(agreementId, tokenAmount);
    }

    function addUpdateTokens(address assetOwner, bytes32 agreementId, uint96 tokenAmount) external onlyContracts {
        ServiceAgreementStorageProxy sasProxy = serviceAgreementStorageProxy;

        sasProxy.setAgreementUpdateTokenAmount(
            agreementId,
            sasProxy.getAgreementUpdateTokenAmount(agreementId) + tokenAmount
        );

        _addTokens(assetOwner, sasProxy.agreementV1U1StorageAddress(), tokenAmount);

        emit ServiceAgreementV1UpdateRewardRaised(agreementId, tokenAmount);
    }

    function isCommitWindowOpen(bytes32 agreementId, uint16 epoch) public view returns (bool) {
        if (serviceAgreementStorageProxy.agreementV1Exists(agreementId)) {
            return commitManagerV1.isCommitWindowOpen(agreementId, epoch);
        } else {
            return commitManagerV1U1.isCommitWindowOpen(agreementId, epoch);
        }
    }

    function getTopCommitSubmissions(
        bytes32 agreementId,
        uint16 epoch
    ) external view returns (ServiceAgreementStructsV1.CommitSubmission[] memory) {
        if (serviceAgreementStorageProxy.agreementV1Exists(agreementId)) {
            return commitManagerV1.getTopCommitSubmissions(agreementId, epoch);
        } else {
            return commitManagerV1U1.getTopCommitSubmissions(agreementId, epoch, 0);
        }
    }

    function submitCommit(ServiceAgreementStructsV1.CommitInputArgs calldata args) external {
        bytes32 agreementId = hashingProxy.callHashFunction(
            args.hashFunctionId,
            abi.encodePacked(args.assetContract, args.tokenId, args.keyword)
        );

        if (serviceAgreementStorageProxy.agreementV1Exists(agreementId)) {
            commitManagerV1.submitCommit(args);
        } else {
            commitManagerV1U1.submitCommit(args);
        }
    }

    function isProofWindowOpen(bytes32 agreementId, uint16 epoch) public view returns (bool) {
        if (serviceAgreementStorageProxy.agreementV1Exists(agreementId)) {
            return proofManagerV1.isProofWindowOpen(agreementId, epoch);
        } else {
            return proofManagerV1U1.isProofWindowOpen(agreementId, epoch);
        }
    }

    function getChallenge(
        address sender,
        address assetContract,
        uint256 tokenId,
        uint16 epoch
    ) public view returns (bytes32 assertionId, uint256 challenge) {
        return proofManagerV1.getChallenge(sender, assetContract, tokenId, epoch);
    }

    function sendProof(ServiceAgreementStructsV1.ProofInputArgs calldata args) external {
        bytes32 agreementId = hashingProxy.callHashFunction(
            args.hashFunctionId,
            abi.encodePacked(args.assetContract, args.tokenId, args.keyword)
        );

        if (serviceAgreementStorageProxy.agreementV1Exists(agreementId)) {
            proofManagerV1.sendProof(args);
        } else {
            proofManagerV1U1.sendProof(args);
        }
    }

    function _addTokens(address assetOwner, address sasAddress, uint96 tokenAmount) internal virtual {
        if (tokenAmount == 0) revert ServiceAgreementErrorsV1U1.ZeroTokenAmount();

        IERC20 tknc = tokenContract;

        if (tknc.allowance(assetOwner, address(this)) < tokenAmount)
            revert ServiceAgreementErrorsV1U1.TooLowAllowance(tknc.allowance(assetOwner, address(this)));
        if (tknc.balanceOf(assetOwner) < tokenAmount)
            revert ServiceAgreementErrorsV1U1.TooLowBalance(tknc.balanceOf(assetOwner));

        tknc.transferFrom(assetOwner, sasAddress, tokenAmount);
    }

    function _generatePseudorandomUint8(address sender, uint8 limit) internal view virtual returns (uint8) {
        return uint8(uint256(keccak256(abi.encodePacked(block.timestamp, sender, block.number))) % limit);
    }
}

contracts/ShardingTable.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.16;

import {ProfileStorage} from "./storage/ProfileStorage.sol";
import {ShardingTableStorage} from "./storage/ShardingTableStorage.sol";
import {StakingStorage} from "./storage/StakingStorage.sol";
import {ContractStatus} from "./abstract/ContractStatus.sol";
import {Initializable} from "./interface/Initializable.sol";
import {Named} from "./interface/Named.sol";
import {Versioned} from "./interface/Versioned.sol";
import {ShardingTableStructs} from "./structs/ShardingTableStructs.sol";
import {NULL} from "./constants/ShardingTableConstants.sol";

contract ShardingTable is Named, Versioned, ContractStatus, Initializable {
    event NodeAdded(uint72 indexed identityId, bytes nodeId, uint96 ask, uint96 stake);
    event NodeRemoved(uint72 indexed identityId, bytes nodeId);

    string private constant _NAME = "ShardingTable";
    string private constant _VERSION = "1.0.1";

    ProfileStorage public profileStorage;
    ShardingTableStorage public shardingTableStorage;
    StakingStorage public stakingStorage;

    // solhint-disable-next-line no-empty-blocks
    constructor(address hubAddress) ContractStatus(hubAddress) {}

    function initialize() public onlyHubOwner {
        profileStorage = ProfileStorage(hub.getContractAddress("ProfileStorage"));
        shardingTableStorage = ShardingTableStorage(hub.getContractAddress("ShardingTableStorage"));
        stakingStorage = StakingStorage(hub.getContractAddress("StakingStorage"));
    }

    function name() external pure virtual override returns (string memory) {
        return _NAME;
    }

    function version() external pure virtual override returns (string memory) {
        return _VERSION;
    }

    function getShardingTable(
        uint72 startingIdentityId,
        uint72 nodesNumber
    ) external view returns (ShardingTableStructs.NodeInfo[] memory) {
        return _getShardingTable(startingIdentityId, nodesNumber);
    }

    function getShardingTable() external view returns (ShardingTableStructs.NodeInfo[] memory) {
        ShardingTableStorage sts = shardingTableStorage;
        return _getShardingTable(sts.head(), sts.nodesCount());
    }

    function pushBack(uint72 identityId) external onlyContracts {
        ProfileStorage ps = profileStorage;
        require(ps.profileExists(identityId), "Profile doesn't exist");

        ShardingTableStorage sts = shardingTableStorage;

        sts.createNodeObject(identityId, NULL, NULL);

        if (sts.tail() != NULL) sts.link(sts.tail(), identityId);

        sts.setTail(identityId);

        if (sts.head() == NULL) sts.setHead(identityId);

        sts.incrementNodesCount();

        emit NodeAdded(
            identityId,
            ps.getNodeId(identityId),
            ps.getAsk(identityId),
            stakingStorage.totalStakes(identityId)
        );
    }

    function pushFront(uint72 identityId) external onlyContracts {
        ProfileStorage ps = profileStorage;
        require(ps.profileExists(identityId), "Profile doesn't exist");

        ShardingTableStorage sts = shardingTableStorage;

        sts.createNodeObject(identityId, NULL, NULL);

        if (sts.head() != NULL) sts.link(identityId, sts.head());

        shardingTableStorage.setHead(identityId);

        if (sts.tail() == NULL) sts.setTail(identityId);

        sts.incrementNodesCount();

        emit NodeAdded(
            identityId,
            ps.getNodeId(identityId),
            ps.getAsk(identityId),
            stakingStorage.totalStakes(identityId)
        );
    }

    function removeNode(uint72 identityId) external onlyContracts {
        ProfileStorage ps = profileStorage;
        require(ps.profileExists(identityId), "Profile doesn't exist");

        ShardingTableStorage sts = shardingTableStorage;

        ShardingTableStructs.Node memory nodeToRemove = sts.getNode(identityId);

        uint72 head = sts.head();
        uint72 tail = sts.tail();

        if ((head == identityId) && (tail == identityId)) {
            sts.setHead(NULL);
            sts.setTail(NULL);
        } else if (tail == identityId) {
            sts.setTail(nodeToRemove.prevIdentityId);
            sts.setNextIdentityId(tail, NULL);
        } else if (head == identityId) {
            sts.setHead(nodeToRemove.nextIdentityId);
            sts.setPrevIdentityId(head, NULL);
        } else {
            sts.link(nodeToRemove.prevIdentityId, nodeToRemove.nextIdentityId);
        }

        sts.deleteNodeObject(identityId);
        sts.decrementNodesCount();

        emit NodeRemoved(identityId, ps.getNodeId(identityId));
    }

    function _getShardingTable(
        uint72 startingIdentityId,
        uint72 nodesNumber
    ) internal view virtual returns (ShardingTableStructs.NodeInfo[] memory) {
        ShardingTableStructs.NodeInfo[] memory nodesPage;
        ShardingTableStorage sts = shardingTableStorage;

        if ((sts.nodesCount() == 0) || (nodesNumber == 0)) {
            return nodesPage;
        }

        ShardingTableStructs.Node memory startingNode = sts.getNode(startingIdentityId);

        require((startingIdentityId == NULL) || (startingNode.identityId != NULL), "Wrong starting Identity ID");

        nodesPage = new ShardingTableStructs.NodeInfo[](nodesNumber);

        ProfileStorage ps = profileStorage;
        StakingStorage ss = stakingStorage;

        nodesPage[0] = ShardingTableStructs.NodeInfo({
            nodeId: ps.getNodeId(startingIdentityId),
            identityId: startingIdentityId,
            ask: ps.getAsk(startingNode.identityId),
            stake: ss.totalStakes(startingNode.identityId)
        });

        uint72 nextIdentityId = startingIdentityId;
        uint72 i = 1;
        while ((i < nodesNumber) && (nextIdentityId != NULL)) {
            nextIdentityId = sts.getNode(nextIdentityId).nextIdentityId;

            nodesPage[i] = ShardingTableStructs.NodeInfo({
                nodeId: ps.getNodeId(nextIdentityId),
                identityId: nextIdentityId,
                ask: ps.getAsk(nextIdentityId),
                stake: ss.totalStakes(nextIdentityId)
            });

            unchecked {
                i += 1;
            }
        }

        return nodesPage;
    }
}

