contracts/Staking.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.16;

import {ShardingTable} from "./ShardingTable.sol";
import {Shares} from "./Shares.sol";
import {IdentityStorage} from "./storage/IdentityStorage.sol";
import {ParametersStorage} from "./storage/ParametersStorage.sol";
import {ProfileStorage} from "./storage/ProfileStorage.sol";
import {ServiceAgreementStorageProxy} from "./storage/ServiceAgreementStorageProxy.sol";
import {ShardingTableStorage} from "./storage/ShardingTableStorage.sol";
import {StakingStorage} from "./storage/StakingStorage.sol";
import {ContractStatus} from "./abstract/ContractStatus.sol";
import {Initializable} from "./interface/Initializable.sol";
import {Named} from "./interface/Named.sol";
import {Versioned} from "./interface/Versioned.sol";
import {ADMIN_KEY} from "./constants/IdentityConstants.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";

contract Staking is Named, Versioned, ContractStatus, Initializable {
    event StakeIncreased(
        uint72 indexed identityId,
        bytes nodeId,
        address indexed staker,
        uint96 oldStake,
        uint96 newStake
    );
    event StakeWithdrawalStarted(
        uint72 indexed identityId,
        bytes nodeId,
        address indexed staker,
        uint96 oldStake,
        uint96 newStake,
        uint256 withdrawalPeriodEnd
    );
    event StakeWithdrawn(uint72 indexed identityId, bytes nodeId, address indexed staker, uint96 withdrawnStakeAmount);
    event AccumulatedOperatorFeeIncreased(
        uint72 indexed identityId,
        bytes nodeId,
        uint96 oldAccumulatedOperatorFee,
        uint96 newAccumulatedOperatorFee
    );
    event OperatorFeeUpdated(uint72 indexed identityId, bytes nodeId, uint8 operatorFee);

    string private constant _NAME = "Staking";
    string private constant _VERSION = "1.0.2";

    ShardingTable public shardingTableContract;
    IdentityStorage public identityStorage;
    ParametersStorage public parametersStorage;
    ProfileStorage public profileStorage;
    StakingStorage public stakingStorage;
    ServiceAgreementStorageProxy public serviceAgreementStorageProxy;
    ShardingTableStorage public shardingTableStorage;
    IERC20 public tokenContract;

    // solhint-disable-next-line no-empty-blocks
    constructor(address hubAddress) ContractStatus(hubAddress) {}

    modifier onlyAdmin(uint72 identityId) {
        _checkAdmin(identityId);
        _;
    }

    function initialize() public onlyHubOwner {
        shardingTableContract = ShardingTable(hub.getContractAddress("ShardingTable"));
        identityStorage = IdentityStorage(hub.getContractAddress("IdentityStorage"));
        parametersStorage = ParametersStorage(hub.getContractAddress("ParametersStorage"));
        profileStorage = ProfileStorage(hub.getContractAddress("ProfileStorage"));
        stakingStorage = StakingStorage(hub.getContractAddress("StakingStorage"));
        serviceAgreementStorageProxy = ServiceAgreementStorageProxy(
            hub.getContractAddress("ServiceAgreementStorageProxy")
        );
        shardingTableStorage = ShardingTableStorage(hub.getContractAddress("ShardingTableStorage"));
        tokenContract = IERC20(hub.getContractAddress("Token"));
    }

    function name() external pure virtual override returns (string memory) {
        return _NAME;
    }

    function version() external pure virtual override returns (string memory) {
        return _VERSION;
    }

    function addStake(address sender, uint72 identityId, uint96 stakeAmount) external onlyContracts {
        _addStake(sender, identityId, stakeAmount);
    }

    function addStake(uint72 identityId, uint96 stakeAmount) external onlyAdmin(identityId) {
        _addStake(msg.sender, identityId, stakeAmount);
    }

    function startStakeWithdrawal(uint72 identityId, uint96 sharesToBurn) external {
        require(sharesToBurn != 0, "Withdrawal amount cannot be 0");

        ProfileStorage ps = profileStorage;
        StakingStorage ss = stakingStorage;

        require(ps.profileExists(identityId), "Profile doesn't exist");

        Shares sharesContract = Shares(ps.getSharesContractAddress(identityId));

        require(sharesToBurn <= sharesContract.balanceOf(msg.sender), "sharesToBurn must be <= balance");

        uint96 oldStake = ss.totalStakes(identityId);
        uint96 stakeWithdrawalAmount = uint96((uint256(oldStake) * sharesToBurn) / sharesContract.totalSupply());
        uint96 newStake = oldStake - stakeWithdrawalAmount;
        uint96 newStakeWithdrawalAmount = ss.getWithdrawalRequestAmount(identityId, msg.sender) + stakeWithdrawalAmount;

        ParametersStorage params = parametersStorage;

        uint256 withdrawalPeriodEnd = block.timestamp + params.stakeWithdrawalDelay();
        ss.createWithdrawalRequest(identityId, msg.sender, newStakeWithdrawalAmount, withdrawalPeriodEnd);
        ss.setTotalStake(identityId, newStake);
        sharesContract.burnFrom(msg.sender, sharesToBurn);

        if (shardingTableStorage.nodeExists(identityId) && (newStake < params.minimumStake())) {
            shardingTableContract.removeNode(identityId);
        }

        emit StakeWithdrawalStarted(
            identityId,
            ps.getNodeId(identityId),
            msg.sender,
            oldStake,
            newStake,
            withdrawalPeriodEnd
        );
    }

    function withdrawStake(uint72 identityId) external {
        ProfileStorage ps = profileStorage;

        require(ps.profileExists(identityId), "Profile doesn't exist");

        StakingStorage ss = stakingStorage;

        uint96 stakeWithdrawalAmount;
        uint256 withdrawalTimestamp;
        (stakeWithdrawalAmount, withdrawalTimestamp) = ss.withdrawalRequests(identityId, msg.sender);

        require(stakeWithdrawalAmount != 0, "Withdrawal hasn't been initiated");
        require(withdrawalTimestamp < block.timestamp, "Withdrawal period hasn't ended");

        ss.deleteWithdrawalRequest(identityId, msg.sender);
        ss.transferStake(msg.sender, stakeWithdrawalAmount);

        emit StakeWithdrawn(identityId, ps.getNodeId(identityId), msg.sender, stakeWithdrawalAmount);
    }

    function addReward(bytes32 agreementId, uint72 identityId, uint96 rewardAmount) external onlyContracts {
        ServiceAgreementStorageProxy sasProxy = serviceAgreementStorageProxy;
        StakingStorage ss = stakingStorage;

        uint96 operatorFee = (rewardAmount * ss.operatorFees(identityId)) / 100;
        uint96 delegatorsReward = rewardAmount - operatorFee;

        ProfileStorage ps = profileStorage;

        uint96 oldAccumulatedOperatorFee = ps.getAccumulatedOperatorFee(identityId);
        uint96 oldStake = ss.totalStakes(identityId);

        if (operatorFee != 0) {
            ps.setAccumulatedOperatorFee(identityId, oldAccumulatedOperatorFee + operatorFee);
            sasProxy.transferAgreementTokens(agreementId, address(ps), operatorFee);
        }

        if (delegatorsReward != 0) {
            ss.setTotalStake(identityId, oldStake + delegatorsReward);
            sasProxy.transferAgreementTokens(agreementId, address(ss), delegatorsReward);

            if (!shardingTableStorage.nodeExists(identityId) && oldStake >= parametersStorage.minimumStake()) {
                shardingTableContract.pushBack(identityId);
            }
        }

        emit AccumulatedOperatorFeeIncreased(
            identityId,
            ps.getNodeId(identityId),
            oldAccumulatedOperatorFee,
            oldAccumulatedOperatorFee + operatorFee
        );

        address sasAddress;
        if (sasProxy.agreementV1Exists(agreementId)) {
            sasAddress = sasProxy.agreementV1StorageAddress();
        } else {
            sasAddress = sasProxy.agreementV1U1StorageAddress();
        }

        emit StakeIncreased(identityId, ps.getNodeId(identityId), sasAddress, oldStake, oldStake + delegatorsReward);
    }

    // solhint-disable-next-line no-empty-blocks
    function slash(uint72 identityId) external onlyContracts {
        // TBD
    }

    function setOperatorFee(uint72 identityId, uint8 operatorFee) external onlyAdmin(identityId) {
        require(operatorFee <= 100, "Operator fee out of [0, 100]");
        stakingStorage.setOperatorFee(identityId, operatorFee);

        emit OperatorFeeUpdated(identityId, profileStorage.getNodeId(identityId), operatorFee);
    }

    function _addStake(address sender, uint72 identityId, uint96 stakeAmount) internal virtual {
        StakingStorage ss = stakingStorage;
        ProfileStorage ps = profileStorage;
        ParametersStorage params = parametersStorage;
        IERC20 tknc = tokenContract;

        uint96 oldStake = ss.totalStakes(identityId);
        uint96 newStake = oldStake + stakeAmount;

        require(ps.profileExists(identityId), "Profile doesn't exist");
        require(tknc.allowance(sender, address(this)) >= stakeAmount, "Allowance < stakeAmount");
        require(newStake <= params.maximumStake(), "Exceeded the maximum stake");

        Shares sharesContract = Shares(ps.getSharesContractAddress(identityId));

        uint256 sharesMinted;
        if (sharesContract.totalSupply() == 0) {
            sharesMinted = stakeAmount;
        } else {
            sharesMinted = ((stakeAmount * sharesContract.totalSupply()) / oldStake);
        }
        sharesContract.mint(sender, sharesMinted);

        ss.setTotalStake(identityId, newStake);
        tknc.transferFrom(sender, address(ss), stakeAmount);

        if (!shardingTableStorage.nodeExists(identityId) && newStake >= params.minimumStake()) {
            shardingTableContract.pushBack(identityId);
        }

        emit StakeIncreased(identityId, ps.getNodeId(identityId), sender, oldStake, newStake);
    }

    function _checkAdmin(uint72 identityId) internal view virtual {
        require(
            identityStorage.keyHasPurpose(identityId, keccak256(abi.encodePacked(msg.sender)), ADMIN_KEY),
            "Admin function"
        );
    }
}

contracts/storage/AssertionStorage.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.16;

import {HubDependent} from "../abstract/HubDependent.sol";
import {Named} from "../interface/Named.sol";
import {Versioned} from "../interface/Versioned.sol";
import {AssertionStructs} from "../structs/AssertionStructs.sol";

contract AssertionStorage is Named, Versioned, HubDependent {
    string private constant _NAME = "AssertionStorage";
    string private constant _VERSION = "1.0.0";

    // assertionId => Assertion
    mapping(bytes32 => AssertionStructs.Assertion) internal assertions;

    // solhint-disable-next-line no-empty-blocks
    constructor(address hubAddress) HubDependent(hubAddress) {}

    function name() external pure virtual override returns (string memory) {
        return _NAME;
    }

    function version() external pure virtual override returns (string memory) {
        return _VERSION;
    }

    function createAssertion(
        bytes32 assertionId,
        uint128 size,
        uint32 triplesNumber,
        uint96 chunksNumber
    ) external onlyContracts {
        assertions[assertionId] = AssertionStructs.Assertion({
            timestamp: block.timestamp,
            size: size,
            triplesNumber: triplesNumber,
            chunksNumber: chunksNumber
        });
    }

    function deleteAssertion(bytes32 assertionId) external onlyContracts {
        delete assertions[assertionId];
    }

    function getAssertion(bytes32 assertionId) external view returns (AssertionStructs.Assertion memory) {
        return assertions[assertionId];
    }

    function getAssertionTimestamp(bytes32 assertionId) external view returns (uint256) {
        return assertions[assertionId].timestamp;
    }

    function getAssertionSize(bytes32 assertionId) external view returns (uint128) {
        return assertions[assertionId].size;
    }

    function getAssertionTriplesNumber(bytes32 assertionId) external view returns (uint32) {
        return assertions[assertionId].triplesNumber;
    }

    function getAssertionChunksNumber(bytes32 assertionId) external view returns (uint96) {
        return assertions[assertionId].chunksNumber;
    }

    function assertionExists(bytes32 assertionId) external view returns (bool) {
        return assertions[assertionId].timestamp != 0;
    }
}

contracts/storage/assets/ContentAssetSto.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.16;

import {AbstractAsset} from "../../abstract/AbstractAsset.sol";
import {Named} from "../../interface/Named.sol";
import {ContentAssetStructs} from "../../structs/assets/ContentAssetStructs.sol";
import {ERC721} from "@openzeppelin/contracts/token/ERC721/ERC721.sol";

contract ContentAssetStorage is AbstractAsset, ERC721 {
    string private constant _NAME = "ContentAssetStorage";
    string private constant _VERSION = "1.0.0";

    uint256 private _tokenId;

    // tokenId => Asset
    mapping(uint256 => ContentAssetStructs.Asset) internal assets;

    // keccak256(tokenId + assertionId + assertionIdIndex) => issuer
    mapping(bytes32 => address) public issuers;

    // solhint-disable-next-line no-empty-blocks
    constructor(address hubAddress) AbstractAsset(hubAddress) ERC721("ContentAssetStorage", "DKG") {}

    function name() public view override(Named, ERC721) returns (string memory) {
        return ERC721.name();
    }

    function version() external pure override returns (string memory) {
        return _VERSION;
    }

    function mint(address to, uint256 tokenId) external onlyContracts {
        _mint(to, tokenId);
    }

    function burn(uint256 tokenId) external onlyContracts {
        _burn(tokenId);
    }

    function generateTokenId() external onlyContracts returns (uint256) {
        unchecked {
            return _tokenId++;
        }
    }

    function deleteAsset(uint256 tokenId) external onlyContracts {
        bytes32[] memory assertionIds = assets[tokenId].assertionIds;
        uint256 assertionIdsLength = assertionIds.length;

        for (uint256 i; i < assertionIdsLength; ) {
            delete issuers[_generateAssetAssertionId(tokenId, assertionIds[i], i)];
            unchecked {
                i++;
            }
        }

        delete assets[tokenId];
    }

    function getAsset(uint256 tokenId) external view returns (ContentAssetStructs.Asset memory) {
        return assets[tokenId];
    }

    function setMutability(uint256 tokenId, bool immutable_) external onlyContracts {
        assets[tokenId].immutable_ = immutable_;
    }

    function isMutable(uint256 tokenId) external view returns (bool) {
        return !assets[tokenId].immutable_;
    }

    function pushAssertionId(uint256 tokenId, bytes32 assertionId) external onlyContracts {
        assets[tokenId].assertionIds.push(assertionId);
    }

    function getAssertionIds(uint256 tokenId) public view override returns (bytes32[] memory) {
        return assets[tokenId].assertionIds;
    }

    function setAssertionIssuer(uint256 tokenId, bytes32 assertionId, address issuer) external onlyContracts {
        issuers[_generateAssetAssertionId(tokenId, assertionId, this.getAssertionIdsLength(tokenId))] = issuer;
    }

    function deleteAssertionIssuer(uint256 tokenId, bytes32 assertionId, uint256 index) external onlyContracts {
        delete issuers[_generateAssetAssertionId(tokenId, assertionId, index)];
    }

    function getAssertionIssuer(
        uint256 tokenId,
        bytes32 assertionId,
        uint256 assertionIndex
    ) external view returns (address) {
        return issuers[keccak256(abi.encodePacked(tokenId, assertionId, assertionIndex))];
    }

    function assertionExists(bytes32 assetAssertionId) external view returns (bool) {
        return issuers[assetAssertionId] != address(0);
    }

    function _generateAssetAssertionId(
        uint256 tokenId,
        bytes32 assertionId,
        uint256 index
    ) internal pure virtual returns (bytes32) {
        return keccak256(abi.encodePacked(tokenId, assertionId, index));
    }
}

contracts/storage/IdentityStorage.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.16;

import {HubDependent} from "../abstract/HubDependent.sol";
import {IERC734Extended} from "../interface/IERC734Extended.sol";
import {Named} from "../interface/Named.sol";
import {Versioned} from "../interface/Versioned.sol";
import {ByteArr} from "../utils/ByteArr.sol";
import {OPERATIONAL_KEY} from "../constants/IdentityConstants.sol";

contract IdentityStorage is IERC734Extended, Named, Versioned, HubDependent {
    using ByteArr for bytes32[];

    string private constant _NAME = "IdentityStorage";
    string private constant _VERSION = "1.0.0";

    uint72 private _identityId;

    struct Identity {
        mapping(bytes32 => Key) keys;
        mapping(uint256 => bytes32[]) keysByPurpose;
    }

    // operationalKey => identityId
    mapping(bytes32 => uint72) public identityIds;
    // identityId => Identity
    mapping(uint72 => Identity) internal identities;

    constructor(address hubAddress) HubDependent(hubAddress) {
        _identityId = 1;
    }

    function name() external pure virtual override returns (string memory) {
        return _NAME;
    }

    function version() external pure virtual override returns (string memory) {
        return _VERSION;
    }

    function deleteIdentity(uint72 identityId) external onlyContracts {
        bytes32[] memory operationalKeys = identities[identityId].keysByPurpose[OPERATIONAL_KEY];
        uint256 operationalKeysNumber = operationalKeys.length;

        for (uint256 i; i < operationalKeysNumber; ) {
            delete identityIds[operationalKeys[i]];
            unchecked {
                i++;
            }
        }

        delete identities[identityId];
    }

    function addKey(uint72 identityId, bytes32 _key, uint256 _purpose, uint256 _type) external override onlyContracts {
        Identity storage identity = identities[identityId];
        identity.keys[_key].purpose = _purpose;
        identity.keys[_key].keyType = _type;
        identity.keys[_key].key = _key;
        identity.keysByPurpose[_purpose].push(_key);

        emit KeyAdded(identityId, _key, _purpose, _type);
    }

    function removeKey(uint72 identityId, bytes32 _key) external override onlyContracts {
        Identity storage identity = identities[identityId];

        uint256 index;
        (index, ) = identity.keysByPurpose[identity.keys[_key].purpose].indexOf(_key);
        identity.keysByPurpose[identity.keys[_key].purpose].removeByIndex(index);

        delete identity.keys[_key];

        emit KeyRemoved(identityId, identity.keys[_key].key, identity.keys[_key].purpose, identity.keys[_key].keyType);
    }

    function keyHasPurpose(uint72 identityId, bytes32 _key, uint256 _purpose) external view override returns (bool) {
        return identities[identityId].keys[_key].purpose == _purpose;
    }

    function getKey(uint72 identityId, bytes32 _key) external view override returns (uint256, uint256, bytes32) {
        return (
            identities[identityId].keys[_key].purpose,
            identities[identityId].keys[_key].keyType,
            identities[identityId].keys[_key].key
        );
    }

    function getKeysByPurpose(uint72 identityId, uint256 _purpose) external view override returns (bytes32[] memory) {
        return identities[identityId].keysByPurpose[_purpose];
    }

    function getIdentityId(address operational) external view returns (uint72) {
        return identityIds[keccak256(abi.encodePacked(operational))];
    }

    function setOperationalKeyIdentityId(bytes32 operationalKey, uint72 identityId) external onlyContracts {
        identityIds[operationalKey] = identityId;
    }

    function removeOperationalKeyIdentityId(bytes32 operationalKey) external onlyContracts {
        delete identityIds[operationalKey];
    }

    function generateIdentityId() external onlyContracts returns (uint72) {
        unchecked {
            return _identityId++;
        }
    }
}

contracts/storage/ParametersStorage.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.16;

import {HubDependent} from "../abstract/HubDependent.sol";
import {Named} from "../interface/Named.sol";
import {Versioned} from "../interface/Versioned.sol";

contract ParametersStorage is Named, Versioned, HubDependent {
    event ParameterChanged(string parameterName, uint256 parameterValue);

    string private constant _NAME = "ParametersStorage";
    string private constant _VERSION = "1.1.0";

    // 0 - minProofWindowOffsetPerc
    // 1 - maxProofWindowOffsetPerc
    // 2 - commitWindowDurationPerc
    // 3 - proofWindowDurationPerc
    // 4 - replacementWindowDurationPerc
    // 5 - finalizationCommitsNumber
    uint8[6] internal args1;

    // 0 - r0
    // 1 - r1
    // 2 - slashingFreezeDuration
    uint32[3] internal args2;
    uint48 public r2;

    // 0 - minimumStake
    // 1 - maximumStake
    uint96[2] internal args3;

    uint128 public epochLength;

    // 0 - stakeWithdrawalDelay
    // 1 - rewardWithdrawalDelay
    uint24[2] internal args4;

    uint16 public updateCommitWindowDuration;

    constructor(address hubAddress) HubDependent(hubAddress) {
        // minimumStake
        args3[0] = 50_000 ether;
        // maximumStake
        args3[1] = 5_000_000 ether;

        r2 = 20;
        // r1
        args2[1] = 8;
        // r0
        args2[0] = 3;

        // minProofWindowOffsetPerc
        args1[0] = 50;
        // maxProofWindowOffsetPerc
        args1[1] = 75;
        // commitWindowDurationPerc
        args1[2] = 25;
        // proofWindowDurationPerc
        args1[3] = 25;
        // replacementWindowDurationPerc
        args1[4] = 0;

        epochLength = 90 days;

        // stakeWithdrawalDelay
        args4[0] = 5 minutes;
        // rewardWithdrawalDelay
        args4[1] = 5 minutes;
        // slashingFreezeDuration
        args2[2] = 730 days;

        updateCommitWindowDuration = 30 minutes;

        // finalizationCommitsNumber
        args1[5] = 3;
    }

    function name() external pure virtual override returns (string memory) {
        return _NAME;
    }

    function version() external pure virtual override returns (string memory) {
        return _VERSION;
    }

    function minimumStake() external view returns (uint96) {
        return args3[0];
    }

    function setMinimumStake(uint96 newMinimumStake) external onlyHubOwner {
        args3[0] = newMinimumStake;

        emit ParameterChanged("minimumStake", newMinimumStake);
    }

    function maximumStake() external view returns (uint96) {
        return args3[1];
    }

    function setMaximumStake(uint96 newMaximumStake) external onlyHubOwner {
        args3[1] = newMaximumStake;

        emit ParameterChanged("maximumStake", newMaximumStake);
    }

    function setR2(uint48 newR2) external onlyHubOwner {
        r2 = newR2;

        emit ParameterChanged("r2", newR2);
    }

    function r1() external view returns (uint32) {
        return args2[1];
    }

    function setR1(uint32 newR1) external onlyHubOwner {
        require(newR1 >= (2 * args2[0] - 1), "R1 should be >= 2*R0-1");

        args2[1] = newR1;

        emit ParameterChanged("r1", newR1);
    }

    function r0() external view returns (uint32) {
        return args2[0];
    }

    function setR0(uint32 newR0) external onlyHubOwner {
